INSERT INTO `ChristianHymns` (`Code`, `stanza_number`, `stanza_text`, `chorus_text01`, `chorus_repetition_number01`, `chorus_text02`, `chorus_repetition_number02`) VALUES
(1, 1, 'Deus prometeu com certeza\r\nChuvas de graça mandar;\r\nEle nos dá fortaleza,\r\nE ricas bênçãos sem par', 'Chuvas de graça,\r\nChuvas pedimos, Senhor;\r\nManda-nos chuvas constantes,\r\nChuvas do Consolador.', 1, NULL, NULL),
(1, 2, 'Cristo nos tem concedido\r\nO santo Consolador,\r\nDe plena paz nos enchido,\r\nPara o reinado do amor.', 'Chuvas de graça,\r\nChuvas pedimos, Senhor;\r\nManda-nos chuvas constantes,\r\nChuvas do Consolador.', 1, NULL, NULL),
(1, 3, 'Dá-nos, Senhor, amplamente,\r\nTeu grande gozo e poder;\r\nFonte de amor permanente,\r\nPõe dentro de nosso ser.', 'Chuvas de graça,\r\nChuvas pedimos, Senhor;\r\nManda-nos chuvas constantes,\r\nChuvas do Consolador.', 1, NULL, NULL),
(1, 4, 'Faze os teus servos piedosos,\r\nDá-lhes virtude e valor,\r\nDando os teus dons preciosos,\r\nDo santo Preceptor.', 'Chuvas de graça,\r\nChuvas pedimos, Senhor;\r\nManda-nos chuvas constantes,\r\nChuvas do Consolador.', 1, NULL, NULL),
(2, 1, 'Oh! que saudosa lembrança\r\nTenho de ti, ó Sião,\r\nTerra que eu tanto amo,\r\nPois és do meu coração.\r\nEu para ti voarei,\r\nQuando o Senhor meu voltar;\r\nPois Ele foi para o céu,\r\nE breve vem me buscar.', 'Sim, eu porfiarei por essa terra de além;\r\nE lá terminarei as muitas lutas de aquém;\r\nLá está meu bom Senhor, ao qual eu desejo ver;\r\nEle é tudo p’ra mim, e sem Ele não posso viver.', 1, NULL, NULL),
(2, 2, 'Bela, mui bela, é a esperança,\r\nDos que vigiam por ti,\r\nPois eles recebem força,\r\nQue só se encontra ali;\r\nOs que procuram chegar\r\nAo teu regaço, ó Sião,\r\nLivres serão de pecar\r\nE de toda a tentação.', 'Sim, eu porfiarei por essa terra de além;\r\nE lá terminarei as muitas lutas de aquém;\r\nLá está meu bom Senhor, ao qual eu desejo ver;\r\nEle é tudo p’ra mim, e sem Ele não posso viver.', 1, NULL, NULL),
(2, 3, 'Diz a Sagrada Escritura,\r\nQue são formosos os pés\r\nDaqueles que boas novas\r\nLevam para os infiéis;\r\nE, se tão belo é falar\r\nDessas grandezas, aqui,\r\nQue não será o gozar\r\nA graça que existe ali!', 'Sim, eu porfiarei por essa terra de além;\r\nE lá terminarei as muitas lutas de aquém;\r\nLá está meu bom Senhor, ao qual eu desejo ver;\r\nEle é tudo p’ra mim, e sem Ele não posso viver.', 1, NULL, NULL),
(3, 1, 'Plena paz e santo gozo,\r\nTenho em ti, ó meu Jesus!\r\nPois eu cri em Tua morte sobre a cruz;\r\nNo Senhor só confiando\r\nNeste mundo viverei,\r\nEntoando aleluias ao meu Rei!', 'Oh! glória ao meu Jesus!\r\nPois é digno de louvor;\r\nÉ meu Rei, meu bom Pastor,\r\nE meu Senhor.\r\nComo os anjos, que O louvam,\r\nEu também O louvarei,\r\nEntoando aleluias ao meu Rei!', 1, NULL, NULL),
(3, 2, 'O amor de Jesus Cristo\r\nÉ mui grande para mim,\r\nPois Sua graça me encheu de amor sem fim.\r\nMeu Jesus foi para a glória,\r\nMas um dia eu O verei,\r\nEntoando aleluias ao meu Rei!', 'Oh! glória ao meu Jesus!\r\nPois é digno de louvor;\r\nÉ meu Rei, meu bom Pastor,\r\nE meu Senhor.\r\nComo os anjos, que O louvam,\r\nEu também O louvarei,\r\nEntoando aleluias ao meu Rei!', 1, NULL, NULL),
(3, 3, 'Este mundo não deseja\r\nTão bondoso Salvador,\r\nNão sabendo agradecer Seu grande amor.\r\nEu, porém, estou gozando\r\nDo favor da Sua lei,\r\nEntoando aleluias ao meu Rei!', 'Oh! glória ao meu Jesus!\r\nPois é digno de louvor;\r\nÉ meu Rei, meu bom Pastor,\r\nE meu Senhor.\r\nComo os anjos, que O louvam,\r\nEu também O louvarei,\r\nEntoando aleluias ao meu Rei!', 1, NULL, NULL),
(3, 4, 'Quando o povo israelita\r\nCom Jesus se concertar,\r\nDando glória ao Seu nome, sem cessar.\r\nNesse tempo, céu e terra\r\nHão de ser a mesma grei,\r\nEntoando aleluias ao meu Rei!', 'Oh! glória ao meu Jesus!\r\nPois é digno de louvor;\r\nÉ meu Rei, meu bom Pastor,\r\nE meu Senhor.\r\nComo os anjos, que O louvam,\r\nEu também O louvarei,\r\nEntoando aleluias ao meu Rei!', 1, NULL, NULL),
(4, 1, 'Não desanimes, Deus proverá;\r\nDeus velará por ti;\r\nSob Suas asas te acolherá;\r\nDeus velará por ti.', 'Deus cuidará de ti\r\nNo teu viver, no teu sofrer;\r\nSeu olhar te acompanhará;\r\nDeus velará por ti.', 1, NULL, NULL),
(4, 2, 'Se o coração palpitar de dor,\r\nDeus velará por ti;\r\nTu já provaste Seu terno amor.\r\nDeus velará por ti.', 'Deus cuidará de ti\r\nNo teu viver, no teu sofrer;\r\nSeu olhar te acompanhará;\r\nDeus velará por ti.', 1, NULL, NULL),
(4, 3, 'Nos desalentos, nas provações,\r\nDeus velará por ti;\r\nLembra-te dEle nas tentações;\r\nDeus velará por ti.', 'Deus cuidará de ti\r\nNo teu viver, no teu sofrer;\r\nSeu olhar te acompanhará;\r\nDeus velará por ti.', 1, NULL, NULL),
(4, 4, 'Tudo o que pedes, Ele fará;\r\nDeus velará por ti;\r\nE o que precisas, não negará.\r\nDeus velará por ti.', 'Deus cuidará de ti\r\nNo teu viver, no teu sofrer;\r\nSeu olhar te acompanhará;\r\nDeus velará por ti.', 1, NULL, NULL),
(4, 5, 'Como estiveres, não temas, vem!\r\nDeus velará por ti;\r\nEle te entende e te ama bem!\r\nDeus velará por ti.', 'Deus cuidará de ti\r\nNo teu viver, no teu sofrer;\r\nSeu olhar te acompanhará;\r\nDeus velará por ti.', 1, NULL, NULL),
(5, 1, 'Espírito, alma e corpo,\r\nOferto a Ti, Senhor,\r\nComo hóstia verdadeira,\r\nEm oblação de amor.', 'Eu tudo a Deus consagro\r\nEm Cristo, o vivo altar;\r\nÓ desce, fogo santo,\r\nDo céu vem tu selar!', 1, NULL, NULL),
(5, 2, 'Sou teu, ó Jesus Cristo!\r\nTeu sangue me comprou;\r\nEu quero a Tua graça,\r\nPois de Ti sempre sou.', 'Eu tudo a Deus consagro\r\nEm Cristo, o vivo altar;\r\nÓ desce, fogo santo,\r\nDo céu vem tu selar!', 1, NULL, NULL),
(5, 3, 'Espírito divino,\r\nDo Pai a promissão;\r\nSedenta a alma pede,\r\nA Ti, a santa unção.', 'Eu tudo a Deus consagro\r\nEm Cristo, o vivo altar;\r\nÓ desce, fogo santo,\r\nDo céu vem tu selar!', 1, NULL, NULL),
(6, 1, 'Tu. na maldição da cruz,\r\nElevaste o Teu amor,\r\nEm rogar, Senhor Jesus:\r\n“Pai, perdoa ao transgressor”.', 'Queres, queres, com vigor,\r\nMuitas almas despertar;\r\nQueres, queres, ó Senhor!\r\nTransformar, remir, curar;\r\nOh! Sim! Oh! Sim!\r\nQueres todos renovar.', 1, NULL, NULL),
(6, 2, 'O Maligno tentador E o autor da perdição,\r\nDo pecado, mal e dor, Da doença e da aflição.', 'Queres, queres, com vigor,\r\nMuitas almas despertar;\r\nQueres, queres, ó Senhor!\r\nTransformar, remir, curar;\r\nOh! Sim! Oh! Sim!\r\nQueres todos renovar.', 1, NULL, NULL),
(6, 3, 'Tu levaste, ó bom Jesus,\r\nO pecado, o mal e a dor,\r\nE a doença sobre a cruz;\r\nÉs o grande Salvador!', 'Queres, queres, com vigor,\r\nMuitas almas despertar;\r\nQueres, queres, ó Senhor!\r\nTransformar, remir, curar;\r\nOh! Sim! Oh! Sim!\r\nQueres todos renovar.', 1, NULL, NULL),
(6, 4, 'Deus é bom, quer sempre o bem Do oprimido pecador;\r\nMal nenhum, em Si, Deus tem;\r\n“Deus é luz, Deus é amor!”', 'Queres, queres, com vigor,\r\nMuitas almas despertar;\r\nQueres, queres, ó Senhor!\r\nTransformar, remir, curar;\r\nOh! Sim! Oh! Sim!\r\nQueres todos renovar.', 1, NULL, NULL),
(7, 1, 'Contra os males deste mundo,\r\nDeus nos vale só;\r\nNão há mal que Deus não cure,\r\nPois de nós tem dó.', 'Cristo cura, sim,\r\nCristo cura, sim.\r\nSeu amor por nós é Imenso;\r\nEle cura, sim!', 1, NULL, NULL),
(7, 2, 'Derramou Seu sangue puro\r\nP’ra remir a mim;\r\nQuando ungido sou de azeite,\r\nSou curado, enfim!', 'Cristo cura, sim,\r\nCristo cura, sim.\r\nSeu amor por nós é Imenso;\r\nEle cura, sim!', 1, NULL, NULL),
(7, 3, 'Só noss’alma é bem segura,\r\nOculta em Jesus;\r\nEle o bálsamo da vida,\r\nDerramou na cruz.', 'Cristo cura, sim,\r\nCristo cura, sim.\r\nSeu amor por nós é Imenso;\r\nEle cura, sim!', 1, NULL, NULL),
(7, 4, 'Glória a Deus! Eterna glória,\r\nDemos-Lhe louvor;\r\nGlória, cânticos e hosanas\r\nDai ao Redentor!', 'Cristo cura, sim,\r\nCristo cura, sim.\r\nSeu amor por nós é Imenso;\r\nEle cura, sim!', 1, NULL, NULL),
(8, 1, 'Cristo Jesus é fiel amigo,\r\nEle só, Ele só.\r\nE nas fraquezas está comigo,\r\nEle só, Ele só.', 'E nas lutas de cada dia,\r\nCristo nunca me deixa só;\r\nPois Ele é meu seguro guia,\r\nEle só, Ele só.', 1, NULL, NULL),
(8, 2, 'Não há amigo mais nobre e digno,\r\nNão, não há; não, não há.\r\nNem mais humilde e mais benigno,\r\nNão, não há; não, não há.', 'E nas lutas de cada dia,\r\nCristo nunca me deixa só;\r\nPois Ele é meu seguro guia,\r\nEle só, Ele só.', 1, NULL, NULL),
(8, 3, 'Ao pecador perdoar anela,\r\nEle só, Ele só;\r\nE pelos Seus santos sempre vela,\r\nEle só, Ele só.', 'E nas lutas de cada dia,\r\nCristo nunca me deixa só;\r\nPois Ele é meu seguro guia,\r\nEle só, Ele só.', 1, NULL, NULL),
(8, 4, 'Deus, em Seu Filho, se há comprazido,\r\nNEle só, nEle só;\r\nMas Sua glória me há repartido,\r\nDEle só, dEle só.', 'E nas lutas de cada dia,\r\nCristo nunca me deixa só;\r\nPois Ele é meu seguro guia,\r\nEle só, Ele só.', 1, NULL, NULL),
(8, 5, 'NEle nós temos um firme guia,\r\nNEle só, nEle só;\r\nA noite enche de alegria,\r\nEle só, Ele só', 'E nas lutas de cada dia,\r\nCristo nunca me deixa só;\r\nPois Ele é meu seguro guia,\r\nEle só, Ele só.', 1, NULL, NULL),
(9, 1, 'Marchai, soldados de Cristo Jesus,\r\nMarchai, marchai e enfrentai\r\nO inimigo do bem e da luz;\r\nSoldados, avançai!', 'Sim, avançar, não recuar;\r\nSem temor, com valor,\r\nMarchai, lutai, pois Cristo vai\r\nNa vanguarda – Avançai!', 1, NULL, NULL),
(9, 2, 'Marchai, soldados, bem alto erguei\r\nO pavilhão da salvação;\r\nA Belial, firmes, ousados, vencei;\r\nVencei a corrupção.', 'Sim, avançar, não recuar;\r\nSem temor, com valor,\r\nMarchai, lutai, pois Cristo vai\r\nNa vanguarda – Avançai!', 1, NULL, NULL),
(9, 3, 'Vossa armadura de guerra tomai,\r\nO elmo, sim, da salvação:\r\nE a espada: a Bíblia levai,\r\nE vencereis, então.', 'Sim, avançar, não recuar;\r\nSem temor, com valor,\r\nMarchai, lutai, pois Cristo vai\r\nNa vanguarda – Avançai!', 1, NULL, NULL),
(9, 4, 'Avante, avante, confiando em Jesus,\r\nFortes sereis por Seu amor,\r\nMarchai invictos, soldados da luz,\r\nJesus é o vencedor.', 'Sim, avançar, não recuar;\r\nSem temor, com valor,\r\nMarchai, lutai, pois Cristo vai\r\nNa vanguarda – Avançai!', 1, NULL, NULL),
(10, 1, 'Glória a Deus, o Pai Eterno,\r\nCujo trono é os céus;\r\nO universo Lhe pertence.\r\nGlória, sim, ao nosso Deus.', 'Eu te louvo! Eu te louvo!\r\nOh! Jesus, meu Salvador!\r\nDai-Lhe glórias, ó remidos!\r\nPois Seu sangue limpa o pecador.', 1, NULL, NULL),
(10, 2, 'Glória ao Filho, mui amado,\r\nQue merece o louvor.\r\nDemos glória ao Seu nome,\r\nAo Cordeiro, ao Redentor.', 'Eu te louvo! Eu te louvo!\r\nOh! Jesus, meu Salvador!\r\nDai-Lhe glórias, ó remidos!\r\nPois Seu sangue limpa o pecador.', 1, NULL, NULL),
(10, 3, 'Demos nós, ao Santo Espírito,\r\nMil louvores por Seu dom;\r\nPor Sua graça tão imensa,\r\nGlórias Ele, Ele é bom!', 'Eu te louvo! Eu te louvo!\r\nOh! Jesus, meu Salvador!\r\nDai-Lhe glórias, ó remidos!\r\nPois Seu sangue limpa o pecador.', 1, NULL, NULL),
(10, 4, 'Glória, glória ao Pai bendito,\r\nGlória, glória a Jesus;\r\nGlória dai ao Santo Espírito.\r\nAo Deus trino, nossa luz.', 'Eu te louvo! Eu te louvo!\r\nOh! Jesus, meu Salvador!\r\nDai-Lhe glórias, ó remidos!\r\nPois Seu sangue limpa o pecador.', 1, NULL, NULL),
(11, 1, 'Ó Cristãos, eia avante, eia avante!\r\nPor Jesus e Sua Igreja a lutar;\r\nCo’a Palavra, essa espada flamante,\r\nVamos, vamos, irmãos, pelejar.', 'Ó Cristão, sempre avante na lida!\r\nO Evangelho com fé a pregar;\r\nAo trabalho o Senhor nos convida,\r\nVamos, pois, com Jesus pelejar.', 1, NULL, NULL),
(11, 2, 'Ó cristãos, eia avante, eia avante!\r\nBoa nova aos perdidos levar;\r\nPor cidades e vilas adiante,\r\nSalvação vamos nós proclamar.', 'Ó Cristão, sempre avante na lida!\r\nO Evangelho com fé a pregar;\r\nAo trabalho o Senhor nos convida,\r\nVamos, pois, com Jesus pelejar.', 1, NULL, NULL),
(11, 3, 'Ó Cristãos, eia avante, eia avante!\r\nPois a aurora já vemos raiar,\r\nDesse dia em que Cristo, triunfante.\r\nNeste mundo há de sempre reinar.', 'Ó Cristão, sempre avante na lida!\r\nO Evangelho com fé a pregar;\r\nAo trabalho o Senhor nos convida,\r\nVamos, pois, com Jesus pelejar.', 1, NULL, NULL),
(11, 4, 'Ó cristãos, eia avante, eia avante!\r\nVexilários da fé vamos ser;\r\nE a vanguarda do exército brilhante\r\nQue gloriosa vitória há de ter!', 'Ó Cristão, sempre avante na lida!\r\nO Evangelho com fé a pregar;\r\nAo trabalho o Senhor nos convida,\r\nVamos, pois, com Jesus pelejar.', 1, NULL, NULL),
(11, 5, 'Ó cristãos, eia avante, eia avante!\r\nCom coragem, sem nada temer;\r\nCom Jesus, divinal comandante,\r\nNós havemos, irmãos, de vencer.', 'Ó Cristão, sempre avante na lida!\r\nO Evangelho com fé a pregar;\r\nAo trabalho o Senhor nos convida,\r\nVamos, pois, com Jesus pelejar.', 1, NULL, NULL),
(12, 1, 'Pecador, vê a luz, brilha para ti,\r\nLá da cruz do Salvador,\r\nOnde a vida deu;\r\ne do lado Seu Corre sangue redentor.', 'Ó vem Já, como estás!\r\nVem agora, ao Salvador;\r\nSua vida deu, tudo padeceu;\r\nNão demores! Vem Já, pecador!', 1, NULL, NULL),
(12, 2, 'Na escuridão do Jardim orou Tão aflito meu Senhor;\r\nQuando pranteou, sangue Seu suou,\r\nNo Getsêmane em dor!', 'Ó vem Já, como estás!\r\nVem agora, ao Salvador;\r\nSua vida deu, tudo padeceu;\r\nNão demores! Vem Já, pecador!', 1, NULL, NULL),
(12, 3, 'Vê o Salvador co’as feridas mãos\r\nTe chamando, ó pecador!\r\nVida eterna têm os que nEle crêem\r\nE aceitam o Seu amor.', 'Ó vem Já, como estás!\r\nVem agora, ao Salvador;\r\nSua vida deu, tudo padeceu;\r\nNão demores! Vem Já, pecador!', 1, NULL, NULL),
(12, 4, 'Vem a Cristo, vem confessando,\r\njá, Teus pecados, e te ouvirá,\r\nE no coração sentirás o perdão,\r\nPois Jesus te perdoará.', 'Ó vem Já, como estás!\r\nVem agora, ao Salvador;\r\nSua vida deu, tudo padeceu;\r\nNão demores! Vem Já, pecador!', 1, NULL, NULL),
(13, 1, 'Jesus comprou-me do mundo aqui,\r\nPalavras doces são para mim;\r\nJesus comprou-me e vai me dar\r\nEterno gozo, celeste lar.\r\nJesus comprou-me e vai me dar\r\nEterno gozo, celeste lar.', NULL, 0, NULL, NULL),
(13, 2, 'Jesus comprou-me da escravidão,\r\nA paz eu gozo por Seu perdão;\r\nSou desprezado um pouco aqui,\r\nMas eu espero gozar ali.\r\nSou desprezado um pouco aqui,\r\nMas eu espero gozar ali.', NULL, 0, NULL, NULL),
(13, 3, 'Jesus comprou-me, e eu fiquei\r\nP’ra sempre livre da dura lei!\r\nAgora espero até Ele vir,\r\nP’ra que me leve ao Seu porvir.\r\nAgora espero até Ele vir,\r\nP’ra que me leve ao Seu porvir.', NULL, 0, NULL, NULL),
(13, 4, 'Ó vinde, amigos, e junto a nós,\r\nOuvi depressa do céu a voz;\r\n- O que demora não chegará,\r\nPorque se perde e morrerá.\r\n- O que demora não chegará,\r\nPorque se perde e morrerá.', NULL, 0, NULL, NULL),
(13, 5, 'Jesus comprou-me e me dará\r\nLugar às mesas, que esperam lá.\r\nOuve o convite, ó pecador,\r\nPois Jesus Cristo é o Salvador.\r\nOuve o convite, ó pecador,\r\nPois Jesus Cristo é o Salvador.', NULL, 0, NULL, NULL),
(14, 1, 'Oh! que gozo sinto em Jesus,\r\nQue por mim expirou na cruz;\r\nA minh’alma deu divina luz,\r\nGlória a meu Jesus!', 'Quanta glória velo em Jesus,\r\nGozo santo e bendita luz,\r\nCujos raios brilham lá na cruz,\r\nNa qual ,morreu Jesus.\r\nSempre avante quero eu andar,\r\nTé no céu chegar.', 1, NULL, NULL),
(14, 2, 'Livre do pecado já estou,\r\nDo abismo me retirou,\r\nSobre a rocha eterna me firmou;\r\nGlória a meu Jesus!', 'Quanta glória velo em Jesus,\r\nGozo santo e bendita luz,\r\nCujos raios brilham lá na cruz,\r\nNa qual ,morreu Jesus.\r\nSempre avante quero eu andar,\r\nTé no céu chegar.', 1, NULL, NULL),
(14, 3, 'Divinal louvor Jesus me deu;\r\nHarmonia que vem do céu;\r\nCantarei, alegre, Cristo é meu,\r\nGlória a meu Jesus!', 'Quanta glória velo em Jesus,\r\nGozo santo e bendita luz,\r\nCujos raios brilham lá na cruz,\r\nNa qual ,morreu Jesus.\r\nSempre avante quero eu andar,\r\nTé no céu chegar.', 1, NULL, NULL),
(14, 4, 'Esta nova hei de proclamar;\r\nMuitos hão de assim chegar,\r\nE a glória receber, sem par,\r\nGlória a meu Jesus!', 'Quanta glória velo em Jesus,\r\nGozo santo e bendita luz,\r\nCujos raios brilham lá na cruz,\r\nNa qual ,morreu Jesus.\r\nSempre avante quero eu andar,\r\nTé no céu chegar.', 1, NULL, NULL),
(15, 1, 'Oh! quão cego andei e perdido vaguei,\r\nLonge, longe do meu Salvador!\r\nMas do céu Ele desceu, e Seu sangue verteu\r\nPra salvar um tão pobre pecador.', 'Foi na cruz, foi na cruz,\r\nonde um dia eu vi\r\nMeu pecado castigado em Jesus;\r\nFoi ali, pela fé, que os olhos abri,\r\nE agora me alegro em Sua luz', 1, NULL, NULL),
(15, 2, 'Eu ouvia falar dessa graça sem par,\r\nQue do céu trouxe nosso Jesus;\r\nMas eu surdo me fiz, converter-me não quis\r\nAo Senhor, que por mim morreu na cruz.', 'Foi na cruz, foi na cruz,\r\nonde um dia eu vi\r\nMeu pecado castigado em Jesus;\r\nFoi ali, pela fé, que os olhos abri,\r\nE agora me alegro em Sua luz', 1, NULL, NULL),
(15, 3, 'Mas um dia senti meu pecado, e vi\r\nSobre mim a espada da lei;\r\nApressado fugi, em Jesus me escondi,\r\nE abrigo seguro nEle achei.', 'Foi na cruz, foi na cruz,\r\nonde um dia eu vi\r\nMeu pecado castigado em Jesus;\r\nFoi ali, pela fé, que os olhos abri,\r\nE agora me alegro em Sua luz', 1, NULL, NULL),
(15, 4, 'Quão ditoso, então, este meu coração,\r\nConhecendo o excelso amor\r\nQue levou meu Jesus a sofrer lá na cruz;\r\nP’ra salvar a um tão pobre pecador.', 'Foi na cruz, foi na cruz,\r\nonde um dia eu vi\r\nMeu pecado castigado em Jesus;\r\nFoi ali, pela fé, que os olhos abri,\r\nE agora me alegro em Sua luz', 1, NULL, NULL),
(16, 1, 'Posso tendo as mãos vazias,\r\nCom Jesus eu me encontrar?\r\nNada fiz, e vão-se os dias,\r\nQue Lhe posso apresentar?', 'Posso tendo as mãos vazias,\r\nCom Jesus, eu me encontrar?\r\nQuantas almas poderia\r\nAo Senhor apresentar?', 1, NULL, NULL),
(16, 2, 'Não mais temerei a morte;\r\nVencerei por salvo estar;\r\nQual será a minha sorte,\r\nSe no céu vazio entrar?', 'Posso tendo as mãos vazias,\r\nCom Jesus, eu me encontrar?\r\nQuantas almas poderia\r\nAo Senhor apresentar?', 1, NULL, NULL),
(16, 3, 'No celeste lar entrando,\r\nComo irei ao Salvador?\r\nQuantas almas irei levando,\r\nPara meu fiel Senhor?', 'Posso tendo as mãos vazias,\r\nCom Jesus, eu me encontrar?\r\nQuantas almas poderia\r\nAo Senhor apresentar?', 1, NULL, NULL),
(16, 4, 'Do pecado, preso em elos,\r\nPassei anos em vão labor;\r\nQuem me dera reavê-los,\r\nP’ra servir ao meu Senhor.', 'Posso tendo as mãos vazias,\r\nCom Jesus, eu me encontrar?\r\nQuantas almas poderia\r\nAo Senhor apresentar?', 1, NULL, NULL),
(16, 5, 'Despertemos, Já é dia;\r\nTrabalhemos, com fervor;\r\nE levemos, com alegria,\r\nMuitas almas ao Senhor.', 'Posso tendo as mãos vazias,\r\nCom Jesus, eu me encontrar?\r\nQuantas almas poderia\r\nAo Senhor apresentar?', 1, NULL, NULL),
(17, 1, 'Nas horas que passo pensando em Jesus\r\nAs trevas desfaço, buscando a luz;\r\nQue horas de vida, tão doces p’ra mim,\r\nJesus me convida, que eu suba p’ra Si!', NULL, 0, NULL, NULL),
(17, 2, 'Da vida voando, sem nenhum temor;\r\nAcima buscando do véu o amor;\r\nQue doce ventura, que aspecto feliz,\r\nQue nova natura minh’alma bendiz.', NULL, 0, NULL, NULL),
(17, 3, 'Do mar o bramido, da brisa o langor\r\nDa ave o carpido de doce amor,\r\nMe falam sentidos acordes dos céus,\r\nMe trazem aos ouvidos os hinos de Deus!', NULL, 0, NULL, NULL),
(17, 4, 'Minh’alma ansiosa já quer percorrer\r\nA senda gloriosa que eu hei de ver;\r\nQue coisa tão bela; oh! que luz sem véu!\r\nJesus me revela mistérios do céu.', NULL, 0, NULL, NULL),
(18, 1, 'Grata nova Deus proclama\r\nHoje, ao mundo pecador!\r\nDoce nova revelada,\r\nLá na cruz do Salvador;\r\nCego e desviado, o homem,\r\nDos caminhos do Senhor,\r\nDesconhece e desconfia\r\nDeste Deus, o Deus de amor.', 'Grata nova, doce nova,\r\nVem dos lábios do Senhor;\r\nEscutai com alegria:\r\n“Deus é luz, Deus é amor”.', 1, NULL, NULL),
(18, 2, 'Com ofertas e obras mortas,\r\nSacrifícios sem valor,\r\nEnganado, pensa o homem,\r\nPropiciar Seu Criador,\r\nMeios de salvar-se inventa;\r\nClama, roga em seu favor,\r\nA supostos mediadores,\r\nDesprezando o Deus de amor.', 'Grata nova, doce nova,\r\nVem dos lábios do Senhor;\r\nEscutai com alegria:\r\n“Deus é luz, Deus é amor”.', 1, NULL, NULL),
(18, 3, 'Luz divina, resplandece!\r\nMostra ao triste pecador,\r\nQue na cruz estão unidos\r\nA justiça e o amor.\r\nFala aos corações feridos,\r\nMostra-te, Deus Salvador;\r\nE sem fim, proclamaremos:\r\n“Deus é luz! Deus é amor!”', 'Grata nova, doce nova,\r\nVem dos lábios do Senhor;\r\nEscutai com alegria:\r\n“Deus é luz, Deus é amor”.', 1, NULL, NULL),
(19, 1, 'Eis, a voz de Cristo clama,\r\nSobre o revoltoso mar\r\nDesta vida perigosa:\r\n“Vinde em Mim vos abrigar”.', NULL, 0, NULL, NULL),
(19, 2, 'Quando outrora em Galiléia,\r\nEssa doce voz soou,\r\nLogo, o coração ouvinte,\r\nTudo, pronto, abandonou.', NULL, 0, NULL, NULL),
(19, 3, 'Sobre as vozes sedutoras,\r\nDe mundanas atrações,\r\nClama a meiga voz de Cristo:\r\n“Dai-me vossos corações!”', NULL, 0, NULL, NULL),
(19, 4, 'Quer no meio dos prazeres,\r\nQuer no dia da aflição,\r\nInda clama, paciente:\r\n“Amas-Me tu, ó cristão?”', NULL, 0, NULL, NULL),
(19, 5, 'Na vereda estreita e santa,\r\nJá pisada por Jesus,\r\nA segui-Lo, Ele te chama:\r\n“Vem, cristão, andar na luz!”', NULL, 0, NULL, NULL),
(19, 6, 'Dessa cruz angustiosa,\r\nOnde brilha Seu amor,\r\nOuve a voz que te convida,\r\nOuve e segue, pecador!', NULL, 0, NULL, NULL),
(20, 1, 'Livres de pecado vós quereis ficar?\r\nOlhai p’ra o Cordeiro de Deus!\r\nEle morto foi na cruz, p’ra vos salvar;\r\nOlhai p’ra o Cordeiro de Deus!', 'Olhai p’ra o Cordeiro de Deus,\r\nOlhai p’ra o cordeiro de Deus,\r\nPorque só Ele vos pode salvar.\r\nOlhal p’ra o Cordeiro de Deus!', 1, NULL, NULL),
(20, 2, 'Se estais tentados, em hesitação,\r\nOlhai p’ra o Cordeiro de Deus!\r\nEle encherá o vosso coração.\r\nOlhai p’ra o Cordeiro de Deus!', 'Olhai p’ra o Cordeiro de Deus,\r\nOlhai p’ra o cordeiro de Deus,\r\nPorque só Ele vos pode salvar.\r\nOlhal p’ra o Cordeiro de Deus!', 1, NULL, NULL),
(20, 3, 'Se estais cansados e sem mais vigor,\r\nOlhai pra o Cordeiro de Deus!\r\nEle vos quer dar Seu divina! amor,\r\nOlhai p’ra o Cordeiro de Deus!', 'Olhai p’ra o Cordeiro de Deus,\r\nOlhai p’ra o cordeiro de Deus,\r\nPorque só Ele vos pode salvar.\r\nOlhal p’ra o Cordeiro de Deus!', 1, NULL, NULL),
(20, 4, 'Se na vossa senda sombras vêm cair,\r\nOlhai pra o Cordeiro de Deus!\r\nEle, com Sua graça, tudo quer suprir.\r\nOlhai p’ra o Cordeiro de Deus!', 'Olhai p’ra o Cordeiro de Deus,\r\nOlhai p’ra o cordeiro de Deus,\r\nPorque só Ele vos pode salvar.\r\nOlhal p’ra o Cordeiro de Deus!', 1, NULL, NULL),
(21, 1, 'A gloriosa aurora vem,\r\nDo dia em que lá em Belém\r\nNasceu de Deus, o Filho;\r\nQue belo dia e de luz,\r\nNo qual p’ra nós desceu Jesus,\r\nDo céu mostrando o trilho.\r\nQuantos santos hoje lembram,\r\nE relembram a história\r\nDe Jesus, o Rei da Glória!', NULL, 0, NULL, NULL),
(21, 2, 'Nasceu Jesus lá em Belém,\r\nTrazendo-nos o sumo bem\r\nDo céu – a liberdade;\r\nNos concedeu perfeita paz,\r\nConsolação que satisfaz\r\nPor toda a eternidade!\r\nComo fomos desgarrados,\r\nDesviados e perdidos!\r\nHoje somos redimidos!', NULL, 0, NULL, NULL),
(21, 3, 'Conosco Cristo habitará\r\nCom Seu poder nos velará,\r\nNos dando livramento;\r\nE sempre há de nos guiar\r\nPois Sua graça vai nos dar\r\nEm todo o sofrimento,\r\nScuta: Luta sempre forte.\r\nTé a morte, pois ajuda\r\nTens em Cristo, que não muda!', NULL, 0, NULL, NULL),
(21, 4, 'Jesus desceu p’ra nos salvar,\r\nO sol da graça vem brilhar,\r\nAlegres jubilemos;\r\nJesus, ó nosso bom Pastor,\r\nVem nos guiar com Teu amor;\r\nAli nós pertencemos;\r\nAlmas salvas, perdoadas,\r\nLibertadas hoje oram,\r\nReverentes Te adoram!', NULL, 0, NULL, NULL),
(22, 1, 'Em nossa alma nós tomamos.\r\nO Teu corpo, ó Jesus!\r\nComo oferta voluntária,\r\nQue nos deste lá na cruz.', 'Como pão do céu desceste,\r\nVamos a Ti receber;\r\nPois unidos ao Teu corpo,\r\nNós queremos, sim, viver.', 1, NULL, NULL),
(22, 2, 'Em figura, o Teu sangue\r\nNós bebemos, ó Senhor,\r\nPois só nEle há virtude\r\nP’ra salvar o pecador.', 'Como pão do céu desceste,\r\nVamos a Ti receber;\r\nPois unidos ao Teu corpo,\r\nNós queremos, sim, viver.', 1, NULL, NULL),
(22, 3, 'Vem, concede à Tua Igreja\r\nO perdão do Teu amor,\r\nPara que nos dois emblemas,\r\nContemplemos-Te, Senhor.', 'Como pão do céu desceste,\r\nVamos a Ti receber;\r\nPois unidos ao Teu corpo,\r\nNós queremos, sim, viver.', 1, NULL, NULL),
(22, 4, 'Ó Senhor, por Teu Espírito\r\nEm nós vem já residir,\r\nPra Teu corpo e Teu sangue\r\nNós podermos discernir.', 'Como pão do céu desceste,\r\nVamos a Ti receber;\r\nPois unidos ao Teu corpo,\r\nNós queremos, sim, viver.', 1, NULL, NULL),
(23, 1, 'Cantam os santos com fervor,\r\nA quem por mim foi imolado,\r\nNa cruz, sofrendo o meu pecado.', 'Glória a Jesus!\r\nGlória a Jesus!\r\nGlória a Jesus, o Salvador!', 1, NULL, NULL),
(23, 2, 'Deus é o Justificador\r\nDe quem crer no Seu Enviado\r\nSim, no Seu Filho mui amado.', 'Glória a Jesus!\r\nGlória a Jesus!\r\nGlória a Jesus, o Salvador!', 1, NULL, NULL),
(23, 3, 'Do Seu Pai recebeu louvor,\r\nE com poder foi exaltado,\r\nPor isso é glorificado.', 'Glória a Jesus!\r\nGlória a Jesus!\r\nGlória a Jesus, o Salvador!', 1, NULL, NULL),
(23, 4, 'Do “sangue purificador”,\r\nEu quero sempre ter lembrança,\r\nE nEle ter mais confiança.', 'Glória a Jesus!\r\nGlória a Jesus!\r\nGlória a Jesus, o Salvador!', 1, NULL, NULL),
(23, 5, 'Por nós, na cruz, sofreu a dor,\r\nCom sangue fomos nós comprados,\r\nE do pecado resgatados.', 'Glória a Jesus!\r\nGlória a Jesus!\r\nGlória a Jesus, o Salvador!', 1, NULL, NULL),
(24, 1, 'No Pentecostes sucedeu\r\nO que Jesus falou,\r\nPois de repente lá do céu\r\nUm vento assoprou,\r\nQue veio a casa toda encher\r\nE os corações com mui poder.', 'Poder, poder, poder pentecostal.\r\nÓ vem nos Inflamar,\r\nTambém nos renovar;\r\nÓ vem, sim, vem, ó chama divinal,\r\nTeus servos batizar.', 1, NULL, NULL),
(24, 2, 'Em cada um veio repousar\r\nA preciosa luz,\r\nO Preceptor que veio ficar\r\nCo’os servos de Jesus;\r\nFoi o fogo santo do Senhor.\r\nQue os encheu com Seu vigor.', 'Poder, poder, poder pentecostal.\r\nÓ vem nos Inflamar,\r\nTambém nos renovar;\r\nÓ vem, sim, vem, ó chama divinal,\r\nTeus servos batizar.', 1, NULL, NULL),
(24, 3, 'E começaram a falar, Repletos de poder,\r\nEm outras línguas exaltar\r\nAo que mandou trazer\r\nOs ricos dons do Seu amor\r\nE o poder consolador.', 'Poder, poder, poder pentecostal.\r\nÓ vem nos Inflamar,\r\nTambém nos renovar;\r\nÓ vem, sim, vem, ó chama divinal,\r\nTeus servos batizar.', 1, NULL, NULL),
(24, 4, 'E quem deseja receber\r\nEsta água salutar.\r\nQue é prometida ao que crer\r\nE humilde esperar,\r\nPerseverando em oração,\r\nTerá poder seu coração.', 'Poder, poder, poder pentecostal.\r\nÓ vem nos Inflamar,\r\nTambém nos renovar;\r\nÓ vem, sim, vem, ó chama divinal,\r\nTeus servos batizar.', 1, NULL, NULL),
(25, 1, 'Meu Jesus, Tu és bom;\r\nTu és tudo pra mim!\r\nFoste morto, mas vives em mim;\r\nTu mereces louvor,\r\nÓ Cordeiro de Deus!\r\nTu és tudo, sim, tudo p’ra mim!', NULL, 0, NULL, NULL),
(25, 2, 'Quero a Ti dar louvor,\r\nPois és Rei sobre reis;\r\nTu és tudo, mas nada eu sou;\r\nEnche meu coração\r\nDe ternura e paz;\r\nHonra eterna e glória Te dou.', NULL, 0, NULL, NULL),
(25, 3, 'O Teu nome é amor,\r\nPois Tu amas a mim,\r\nE eu quero andar neste amor;\r\nTriunfante estou,\r\nConfiando em Ti,\r\nMeu Jesus, grande Consolador.', NULL, 0, NULL, NULL),
(25, 4, 'Ó Ungido de Deus,\r\nTrazes paz e perdão;\r\nSalvação, vida, tens para os Teus;\r\nQue mensagem do céu\r\nE de transformação,\r\nEnviada do trono de Deus!', NULL, 0, NULL, NULL),
(26, 1, 'Quão glorioso, cristão, é pensares\r\nNa cidade que não tem igual,\r\nOnde os muros são de puro jaspe.\r\nE as ruas de ouro e cristal;\r\nPensa como será glorioso\r\nVer-se a triunfal multidão.\r\nQue cantando, aguarda a chegada\r\nDos que vencem a tribulação.', NULL, 0, NULL, NULL),
(26, 2, 'Pensa como será glorioso\r\nVer o rio da vida e luz,\r\nCujas margens juncadas de lírios,\r\nSão a glória de nosso Jesus;\r\nHaverá lá perpétua aurora,\r\nPois Deus mesmo a alumiará;\r\nE o Cordeiro, com Sua esposa,\r\nNoite e dia resplandecerá.', NULL, 0, NULL, NULL),
(26, 3, 'Pensa na celestial melodia\r\nQue a terra encherá, de Beulá;\r\nE das harpas a doce harmonia\r\nAo passar o Jordão se ouvirá.\r\nMesmo em dores que levam à morte,\r\nSê constante, não voltes atrás,\r\nTua herança, tua eterna sorte,\r\nÉ Jesus, o Fiel, o Veraz.', NULL, 0, NULL, NULL),
(26, 4, 'Se é glorioso pensar nas grandezas,\r\nNos prazeres que acodem aqui,\r\nQual será desfrutar as riquezas\r\nQue esperam os salvos, ali?\r\nOs encantos do mundo não podem\r\nOfuscar essa glória dalém;\r\nNão almejas viver, ó amigo,\r\nNessa formosa Jerusalém?', NULL, 0, NULL, NULL),
(27, 1, 'Amor, que por amor desceste!\r\nAmor, que por amor morreste!\r\nAh! quanta dor não padeceste,\r\nMeu coração p’ra conquistar,\r\nE meu amor ganhar!', NULL, 0, NULL, NULL),
(27, 2, 'Amor, que com amor seguias!\r\nA mim, que sem amor Tu vias!\r\nOh! quanto amor por mim sentias,\r\nMeu Salvador, meu bom Jesus,\r\nSofrendo sobre a cruz!', NULL, 0, NULL, NULL),
(27, 3, 'Amor, que tudo me perdoas!\r\nAmor, que até mesmo abençoas!\r\nUm réu de quem Te afeiçoas!\r\nPor Ti vencido, ó Salvador,\r\nEis-me aos Teus pés, Senhor!', NULL, 0, NULL, NULL),
(27, 4, 'Amor, que nunca, nunca mudas,\r\nQue nos Teus braços me seguras,\r\nCercando-me de mil venturas!\r\nAceita agora, Salvador.\r\nO meu humilde amor!', NULL, 0, NULL, NULL),
(28, 1, 'Andas carregado de tristeza e dor,\r\nSem nenhum auxílio, nem um Salvador?\r\nOuve a mensagem de teu bom Jesus,\r\nE tem mais coragem, leva a tua cruz.', 'Deus vai te guiar com Sua forte mão;\r\nPodes descansar na tribulação;\r\nSeja tua vida livre de pesar;\r\nEm tristeza e lida Deus vai te guiar.', 1, NULL, NULL),
(28, 2, 'Toda carga por ti, Ele quer levar; Alma dolorida, ouve o Seu falar;\r\nSenda espinhosa Ele já andou,\r\nMorte afrontosa por ti suportou.', 'Deus vai te guiar com Sua forte mão;\r\nPodes descansar na tribulação;\r\nSeja tua vida livre de pesar;\r\nEm tristeza e lida Deus vai te guiar.', 1, NULL, NULL),
(28, 3, 'Se estás tentado, Deus te ajudará, Sempre confiando, te libertará;\r\nAs pisadas segue do teu Salvador, Crendo em Deus, prossegue, seja como for.', 'Deus vai te guiar com Sua forte mão;\r\nPodes descansar na tribulação;\r\nSeja tua vida livre de pesar;\r\nEm tristeza e lida Deus vai te guiar.', 1, NULL, NULL),
(29, 1, 'Oh! que precioso sangue,\r\nMeu Senhor verteu,\r\nQuando, para resgatar-nos,\r\nPadeceu!', NULL, 0, NULL, NULL),
(29, 2, 'Oh! que precioso sangue,\r\nSangue de Jesus,\r\nQue por nós, foi derramado\r\nSobre a cruz!', NULL, 0, NULL, NULL),
(29, 3, 'Oh! que precioso sangue,\r\nSangue remidor!\r\nSim, com este nos remiste,\r\nRedentor!', NULL, 0, NULL, NULL),
(29, 4, 'Oh! que precioso sangue,\r\nSangue expiador!\r\nEis o que da pena !ivra\r\nO malfeitor!', NULL, 0, NULL, NULL),
(29, 5, 'Oh! que precioso sangue,\r\nPurificador!\r\nQue de toda a mancha lava\r\nO pecador!', NULL, 0, NULL, NULL),
(29, 6, 'Oh! que precioso sangue,\r\nFala-nos de paz;\r\nTudo quanto a lei exige,\r\nSatisfaz!', NULL, 0, NULL, NULL),
(29, 7, 'Oh! que precioso sangue.\r\nPor Ele entrarei\r\nSem receio, na presença\r\nDo meu Rei!', NULL, 0, NULL, NULL),
(29, 8, 'Oh! que precioso sangue,\r\nDo bom Salvador!\r\nHoje, a todos manifesta\r\nSeu amor!', NULL, 0, NULL, NULL),
(30, 1, 'Não sou meu! Por Cristo salvo,\r\nQue por mim morreu na cruz,\r\nEu confesso alegremente,\r\nQue pertenço ao bom Jesus.', 'Não sou meu! Oh! Não sou meu!\r\nBom Jesus, sou todo Teu!\r\nHoje mesmo e para sempre,\r\nBom Jesus, sou todo Teu!', 1, NULL, NULL),
(30, 2, 'Não sou meu! Por Ele remido,\r\nQuando o sangue derramou;\r\nNa Sua graça confiando,\r\nQue minh’alma resgatou.', 'Não sou meu! Oh! Não sou meu!\r\nBom Jesus, sou todo Teu!\r\nHoje mesmo e para sempre,\r\nBom Jesus, sou todo Teu!', 1, NULL, NULL),
(30, 3, 'Jamais meu! A Ti confio\r\nTudo quanto chamo meu;\r\nTudo nas Tuas mãos entrego;\r\nMeu Senhor, sou todo Teu!', 'Não sou meu! Oh! Não sou meu!\r\nBom Jesus, sou todo Teu!\r\nHoje mesmo e para sempre,\r\nBom Jesus, sou todo Teu!', 1, NULL, NULL),
(30, 4, 'Jamais meu! Ó santifica\r\nTudo quanto sou, Senhor!\r\nDa vaidade e da soberba,\r\nVem livrar-me, Salvador.', 'Não sou meu! Oh! Não sou meu!\r\nBom Jesus, sou todo Teu!\r\nHoje mesmo e para sempre,\r\nBom Jesus, sou todo Teu!', 1, NULL, NULL),
(31, 1, 'Por mim sofreu o Salvador,\r\nGlória! Glória ao meu Jesus!\r\nLouvai, comigo, ao Redentor!\r\nGlória! Glória ao meu Jesus!', 'Jesus! Jesus! Ó Salvador;\r\nÉ doce o nome do Senhor;\r\nAbrasa -me com santo amor;\r\nGlória! Glória ao meu Senhor.', 1, NULL, NULL),
(31, 2, 'C’oa meus pecados carregou,\r\nGlória! Glória ao meu Jesus!\r\nE sobre a cruz me resgatou.\r\nGlória! Glória ao meu Jesus!', 'Jesus! Jesus! Ó Salvador;\r\nÉ doce o nome do Senhor;\r\nAbrasa -me com santo amor;\r\nGlória! Glória ao meu Senhor.', 1, NULL, NULL),
(31, 3, 'Eu sei que perdoado estou,\r\nGlória! Glória ao meu Jesus!\r\nE, com certeza, ao céu eu vou!\r\nGlória! Glória ao meu Jesus!', 'Jesus! Jesus! Ó Salvador;\r\nÉ doce o nome do Senhor;\r\nAbrasa -me com santo amor;\r\nGlória! Glória ao meu Senhor.', 1, NULL, NULL),
(31, 4, 'E, quando a guerra aqui findar,\r\nGlória! Glória ao meu Jesus!\r\nNo céu, melhor, eu vou cantar,\r\nGlória! Glória ao meu Jesus!', 'Jesus! Jesus! Ó Salvador;\r\nÉ doce o nome do Senhor;\r\nAbrasa -me com santo amor;\r\nGlória! Glória ao meu Senhor.', 1, NULL, NULL),
(32, 1, 'Quem do céu por mim desceu,\r\nTudo em meu lugar sofreu,\r\nE por mim na cruz morreu?\r\nFoi Cristo! Foi Cristo!', 'Os meus pecados expiou:\r\nDe toda a pena me livrou!\r\nDa maldição me resgatou,\r\nMeu Cristo! Meu Cristo!', 1, NULL, NULL),
(32, 2, 'Quem me trouxe, qual pastor,\r\nSeu cordeiro com amor?\r\nQuem quis ser meu Salvador?\r\nFoi Cristo! Foi Cristo!', 'Os meus pecados expiou:\r\nDe toda a pena me livrou!\r\nDa maldição me resgatou,\r\nMeu Cristo! Meu Cristo!', 1, NULL, NULL),
(32, 3, 'Quem com branda compaixão,\r\nDerreteu meu coração?\r\nDeu-me plena salvação?\r\nFoi Cristo! Foi Cristo!', 'Os meus pecados expiou:\r\nDe toda a pena me livrou!\r\nDa maldição me resgatou,\r\nMeu Cristo! Meu Cristo!', 1, NULL, NULL),
(32, 4, 'Quem diz: “Não te deixarei,\r\nNem te desampararei,\r\nSempre te socorrerei?”\r\nÉ Cristo! E Cristo!', 'Os meus pecados expiou:\r\nDe toda a pena me livrou!\r\nDa maldição me resgatou,\r\nMeu Cristo! Meu Cristo!', 1, NULL, NULL),
(32, 5, 'Quem é digno de louvor?\r\nQuem merece o meu amor?\r\nÉ Jesus, meu Salvador,\r\nMeu Cristo! Meu Cristo!', 'As minhas trevas dissipou!\r\nMinha alma enferma Ele sarou!\r\nMeu coração Ele alegrou!\r\nMeu Cristo! Meu Cristo!', 1, NULL, NULL),
(33, 1, 'Com Tua mão, segura bem a minha,\r\nPois eu tão fraco sou, ó Salvador!\r\nQue não me atrevo a dar nem um só passo\r\nSem Teu amparo, meu Jesus Senhor!', NULL, 0, NULL, NULL),
(33, 2, 'Com Tua mão, segura bem a minha,\r\nE mais e mais unido a Ti, Jesus.\r\nÓ traze-me, que nunca me desvie\r\nDe Ti, Senhor, a minha vida e luz!', NULL, 0, NULL, NULL),
(33, 3, 'Com Tua mão, segura bem a minha,\r\nE, pelo mundo, alegre seguirei:\r\nMesmo onde as sombras caem mais escuras\r\nTeu rosto vendo, nada temerei.', NULL, 0, NULL, NULL),
(33, 4, 'E, se chegar à beira desse rio,\r\nQue Tu por mim quiseste atravessar,\r\nCom Tua mão segura bem a minha,\r\nE sobre a morte eu hei de triunfar.', NULL, 0, NULL, NULL),
(33, 5, 'Quando voltares esses céus rompendo,\r\nsegura bem a minha mão, Senhor,\r\nE, meu Jesus, ó leva-me contigo,\r\nPara onde eu goze Teu eterno amor.', NULL, 0, NULL, NULL),
(34, 1, 'Milhares de milhares em refulgente luz!\r\nEis os guerreiros santos, milícia de Jesus!\r\nCompleta, sim, completa sua longa luta aqui,\r\nCom Cristo, seu Senhora Rei, vão descansar ali;', NULL, 0, NULL, NULL),
(34, 2, 'Que doces sinfonias encham a terra e o céu!\r\nQue coros d’aleluias rompem além do véu!\r\nE que chegou o dia, o dia triunfal,\r\nE Cristo reinará, enfim, em glória divina!.', NULL, 0, NULL, NULL),
(34, 3, 'Então, não há mais choro, nem há mais tentação;\r\nAs dores, as tristezas p’ra sempre fugirão,\r\nE os remidos todos verão seu Salvador;\r\nE transformados, todos, serão como o Senhor.', NULL, 0, NULL, NULL),
(35, 1, 'O grande amor de meu Jesus\r\nPor mim morrendo sobre a cruz,\r\nDa perdição, pra me salvar;\r\nQuem poderá contar?', 'Quem pode o Seu amor contar?\r\nQuem pode o Seu amor contar?\r\nO grande amor do Salvador,\r\nQuem poderá contar?', 1, NULL, NULL),
(35, 2, 'O cálice que Jesus bebeu,\r\nA maldição que padeceu,\r\nTudo por mim, p’ra me salvar,\r\nQuem poderá contar?', 'Quem pode o Seu amor contar?\r\nQuem pode o Seu amor contar?\r\nO grande amor do Salvador,\r\nQuem poderá contar?', 1, NULL, NULL),
(35, 3, 'A zombaria tão cruel,\r\nA cruz sangrenta, o amargo fel,\r\nQue Ele sofreu pra me salvar;\r\nQuem poderá contar?', 'Quem pode o Seu amor contar?\r\nQuem pode o Seu amor contar?\r\nO grande amor do Salvador,\r\nQuem poderá contar?', 1, NULL, NULL),
(35, 4, 'Incomparável Salvador!\r\nQuão inefável Teu amor!\r\nQuão impossível de sondar!\r\nImenso e sem par!', 'Quem pode o Seu amor contar?\r\nQuem pode o Seu amor contar?\r\nO grande amor do Salvador,\r\nQuem poderá contar?', 1, NULL, NULL),
(36, 1, 'Da linda pátria estou bem longe;\r\nCansado estou;\r\nEu tenho de Jesus saudada,\r\nOh, quando é que eu vou?\r\nPassarinhos, belas flores,\r\nQuerem m’encantar;\r\nSão vãos terrestres esplendores,\r\nMas contemplo o meu lar.', NULL, 0, NULL, NULL),
(36, 2, 'Jesus me deu a Sua promessa;\r\nMe vem buscar;\r\nMeu coração está com pressa,\r\nEu quero já voar.\r\nMeus pecados foram muitos,\r\nMui culpado sou;\r\nPorém, Seu sangue põe-me limpo;\r\nEu para pátria vou.', NULL, 0, NULL, NULL),
(36, 3, 'Qual filho de seu lar saudoso,\r\nEu quero ir;\r\nQual passarinho para o ninho,\r\nPra os braços Seus fugir;\r\nÉ fiel – Sua vinda é certa,\r\nQuando… Eu não sei.\r\nMas Ele manda estar alerta;\r\nDo exílio voltarei.', NULL, 0, NULL, NULL),
(36, 4, 'Sua vinda aguardo eu cantando;\r\nMeu lar no céu;\r\nSeus passos hei de ouvir soando\r\nAlém do escuro véu.\r\nPassarinhos, belas flores,\r\nQuerem m’encantar;\r\nSão vãos terrestres esplendores,\r\nMas contemplo o meu lar.', NULL, 0, NULL, NULL),
(37, 1, 'Oh! que descanso em Jesus encontrei!\r\nCristo p’ra mim! Cristo p’ra mim!\r\nOh! que tesouros infindos achei!\r\nCristo p’ra mim! Cristo p’ra mim!\r\n‘Scolham os outros o mundo pra si.\r\nBusquem riquezas, delicias aqui;\r\nEu acolherei, ó Jesus, sempre a Ti!\r\nCristo p’ra mim! Cristo p’ra mim!', NULL, 0, NULL, NULL),
(37, 2, 'Quer na aflição, na doença ou na dor:\r\nCristo p’ra mim! Cristo p’ra mim!\r\nQuer na saúde, na força ou vigor;\r\nCristo p’ra mim! Cristo p’ra mim!\r\nSempre ao meu lado pra me socorrer,\r\nCom Seu amor, sim, e com Seu poder;\r\nEm cada transe pronto a me valer;\r\nCristo p’ra mim! Cristo p’ra mim!', NULL, 0, NULL, NULL),
(37, 3, 'No dia amargo da perseguição:\r\nCristo p’ra mim! Cristo p’ra mim!\r\nNas duras provas e na tentação:\r\nCristo p’ra mim! Cristo p’ra mim!\r\nEle, o pecado e o mundo venceu\r\nQuando, por mim, no Calvário morreu;\r\nE da vitória a certeza me deu;\r\nCristo p’ra mim! Cristo p’ra mim!', NULL, 0, NULL, NULL),
(37, 4, 'Quando no vale da morte eu entrar,\r\nCristo p’ra mim! Cristo p’ra mim!\r\nQuando perante meu Deus m’encontrar:\r\nCristo p’ra mim! Cristo p’ra mim!\r\nSó no Teu sangue confio, Senhor,\r\nSó no Teu sempre imutável amor!\r\nInda outra vez cantarei, Salvador:\r\nCristo p’ra mim! Cristo p’ra mim!', NULL, 0, NULL, NULL),
(38, 1, 'Proclamei a todo o mundo que o Senhor é Rei!\r\nProclamai! Proclamai!\r\nProclamei que suave e doce é Sua santa lei!\r\nProclamai! Proclamai!\r\nProclamai como Ele se chama Salvador Jesus,\r\nComo Ele por nós morreu na ensangüentada cruz,\r\nComo Ele – o Cordeiro – sobre o trono está\r\nO Deus de toda graça, que de graça tudo dá.', 'Proclama! a todo o mundo\r\nque o Senhor é Rei!\r\nProclamai! Proclamai!\r\nProclama! que suave e doce\r\né Sua santa lei!\r\nProclamai! Proclamai!', 1, NULL, NULL),
(38, 2, 'Proclamei que reina em graça nosso Salvador!\r\nProclamei! Proclamei!\r\nQue por cetro de Seu reino ele tem o amor!\r\nProclamei! Proclamei!\r\nAnunciei aos tristes que Ele vive lá;\r\nE e todos os cansados que descanso Ele dá;\r\nContei aos pecadores que Ele veio salvar,\r\nE a todos os cativos que Ele os pode libertar.', 'Proclama! a todo o mundo\r\nque o Senhor é Rei!\r\nProclamai! Proclamai!\r\nProclama! que suave e doce\r\né Sua santa lei!\r\nProclamai! Proclamai!', 1, NULL, NULL),
(38, 3, 'Proclamei que Ele, do céu em breve descerá!\r\nProclamei! Proclamei!\r\nE com todos os Seus santos aparecerá!\r\nProclamei! Proclamei!\r\nQue, sem demora, venham todos se render\r\nE, com amor, em tudo e Cristo obedecer\r\nQue estejam todos prontos quando Ele voltar,\r\nE alegres, naquele dia, “Rei dos reis”, o aclamar', 'Proclama! a todo o mundo\r\nque o Senhor é Rei!\r\nProclamai! Proclamai!\r\nProclama! que suave e doce\r\né Sua santa lei!\r\nProclamai! Proclamai!', 1, NULL, NULL),
(39, 1, 'Bendito seja o Cordeiro,\r\nQue na cruz por nós padeceu!\r\nBendito seja o Seu sangue,\r\nQue por nós, ali Ele verteu!\r\nEis nesse sangue levados,\r\nCom roupas que tão alvas são,\r\nOs pecadores remidos,\r\nQue perante seu Deus já estão!', 'Alvo mais que a neve!\r\nAlvo mais que a neve!\r\nSim, nesse sangue lavado,\r\nMais alvo que a neve serei.', 1, NULL, NULL),
(39, 2, 'Quão espinhosa a coroa\r\nQue Jesus por nós suportou!\r\nOh! Quão profundas as chagas,\r\nQue nos provam quanto Ele amou!\r\nEis nessas chagas pureza\r\nPara o maior pecador!\r\nPois que mais alvo que a neve,\r\nO Teu sangue nos torna, Senhor!', 'Alvo mais que a neve!\r\nAlvo mais que a neve!\r\nSim, nesse sangue lavado,\r\nMais alvo que a neve serei.', 1, NULL, NULL),
(39, 3, 'Se nós a Ti confessarmos,\r\nE seguirmos na Tua luz,\r\nTu não somente perdoas,\r\nPurificas também, ó Jesus;\r\nSim, e de todo o pecado!\r\nQue maravilha de amor!\r\nPois que mais alvo que a neve.\r\nO Teu sangue nos torna, Senhor.', 'Alvo mais que a neve!\r\nAlvo mais que a neve!\r\nSim, nesse sangue lavado,\r\nMais alvo que a neve serei.', 1, NULL, NULL),
(40, 1, 'Que se diz dos teus encantos,\r\nÓ cidade do bom Deus!\r\nQue soberba te levantas\r\nEntre as glórias desses céus,\r\nDesde o mundo preparada\r\nPara Cristo e para os Seus.', NULL, 0, NULL, NULL),
(40, 2, 'São de jaspe adamantino\r\nOs teus muros, ó Sião!\r\nSão douradas essas ruas,\r\nQue os remidos pisarão!\r\nDe celeste luz banhadas,\r\nRefulgentes sempre estão!', NULL, 0, NULL, NULL),
(40, 3, 'Pelo próprio Deus fundada\r\nSobre a rocha secular\r\nQuão seguras tuas portas!\r\nQuem as pode ameaçar?\r\nIgualmente, quão seguro\r\nQuem por ela lá entrar!', NULL, 0, NULL, NULL),
(40, 4, 'Quão felizes são teus filhos,\r\nVendo a face do Senhor!\r\nNunca mais a fome sentem,\r\nNunca sentem mais a dor,\r\nCom os anjos desfrutando\r\nPleno gozo, paz e amor!', NULL, 0, NULL, NULL),
(40, 5, 'O trono do Cordeiro,\r\nNoite e dia, sem cessar\r\nCorre o rio de água viva,\r\nPara todos saciar!\r\nLá com Cristo, vão teus filhos,\r\nSatisfeitos descansar!', NULL, 0, NULL, NULL),
(40, 6, 'Se, por Teu amor infindo,\r\nDe Sião eu filho for,\r\nZombe o mundo e escarneça,\r\nNada importa, Salvador,\r\nPois que do Teu gozo eterno\r\nMesmo aqui tenho o penhor!', NULL, 0, NULL, NULL),
(41, 1, 'A Cristo coroai! Que por nós encarnou,\r\nE Deus, o Santo Deus e Pai, aos homens revelou.\r\nEis Sua compaixão! Eis Sua mansidão!\r\nQuem vê a Cristo, vê ao Pai; sim, vê Seu coração.', NULL, 0, NULL, NULL),
(41, 2, 'A Cristo coroai! De tudo o Criador,\r\nO Filho do eterno Deus, do mundo, o Salvador, Jesus Emanuel,\r\no grande Redentor!\r\nEm busca dos perdidos, vem o nosso bom Pastor.', NULL, 0, NULL, NULL),
(41, 3, 'A Cristo coroai! Que sobre a cruz, ganhou\r\nPor nós eterna redenção e para o céu voltou!\r\nEle é o Rei dos reis! O Príncipe da paz!\r\nJesus, da morte, o vencedor, que a Salvação nos traz.', NULL, 0, NULL, NULL),
(41, 4, 'A Cristo coroai! De todos o Senhor,\r\nA quem a multidão dos céus aclama com fervor!\r\nEis o Cordeiro ali, que sobre o trono está!\r\nQue vive e reina lá por nós, e cedo voltará.', NULL, 0, NULL, NULL),
(42, 1, 'Saudai o nome de Jesus!\r\nArcanjos, vos prostrai;\r\nArcanjos, vos prostrai;\r\nAo Filho do eterno Deus,\r\nCom glória, glória,\r\nGlória, glória,\r\nCom glória, coroai!', NULL, 0, NULL, NULL),
(42, 2, 'Ó escolhida geração\r\nDo bom eterno Pai,\r\nDo bom eterno Pai;\r\nAo grande autor da salvação,\r\nCom glória, glória,\r\nGlória, glória,\r\nCom glória, coroai!', NULL, 0, NULL, NULL),
(42, 3, 'Ó perdoados, cujo amor\r\nBem triunfante vai,\r\nBem triunfante vai,\r\nAo Deus Varão, Conquistador,\r\nCom glória, glória,\r\nGlória, glória,\r\nCom glória, coroai!', NULL, 0, NULL, NULL),
(42, 4, 'Ó raças, tribos e nações,\r\nAo Rei dos reis honrai!\r\nAo Rei dos reis honrai!\r\nA quem quebrou vossos grilhões,\r\nCom glória, glória,\r\nGlória, glória,\r\nCom glória, coroai!', NULL, 0, NULL, NULL),
(43, 1, 'Na pátria celeste, de Deus o doce lar,\r\nPrepara Jesus, para os Seus, um lugar,\r\nPois longe do mal, do pecado e da dor,\r\nConsigo p’ra sempre os quer ter seu Senhor.', 'Oh! Doce, doce lar!\r\nAli, com Jesus, vou para sempre descansar', 1, NULL, NULL),
(43, 2, 'Oh! lar sacrossanto de paz e de amor!\r\nAli sobre o trono, verei meu Senhor,\r\nO meigo Cordeiro, reinando em luz,\r\nPor todos louvado, bendito Jesus!', 'Oh! Doce, doce lar!\r\nAli, com Jesus, vou para sempre descansar', 1, NULL, NULL),
(43, 3, 'Que puras delícias se encontram em ti!\r\nQue gozos supremos esperam ali,\r\nAqueles a quem juntos Si Deus quer ter,\r\nE perpetuamente os satisfazer!', 'Oh! Doce, doce lar!\r\nAli, com Jesus, vou para sempre descansar', 1, NULL, NULL),
(43, 4, 'Não são seus prazeres que anseio gozar,\r\nMas, sim, com Jesus para sempre morar!\r\nJamais desonrá-Lo, jamais ofender\r\nA quem, p’ra ganhar-me, por mim quis morrer.', 'Oh! Doce, doce lar!\r\nAli, com Jesus, vou para sempre descansar', 1, NULL, NULL),
(44, 1, 'Meu Senhor, que me salvaste,\r\nTeu, e Teu somente, eu sou;\r\nCom Teu sangue me saraste;\r\nGlória, glória a Ti Te dou.', 'Oh! Que glória! Oh! Que glória!\r\nGozo em meu coração!\r\nEu confio em Jesus,\r\nE, crendo, tenho salvação.\r\nOh! Que glória! Oh! Que glória!\r\nGozo em meu coração!\r\nEu confio em Jesus,\r\nE em Seu sangue achei perdão.', 1, NULL, NULL),
(44, 2, 'Para obter tão grande gozo,\r\nMuito e muito trabalhei;\r\nMas debalde todo esforço,\r\nCrendo só é que o achei.', 'Oh! Que glória! Oh! Que glória!\r\nGozo em meu coração!\r\nEu confio em Jesus,\r\nE, crendo, tenho salvação.\r\nOh! Que glória! Oh! Que glória!\r\nGozo em meu coração!\r\nEu confio em Jesus,\r\nE em Seu sangue achei perdão.', 1, NULL, NULL),
(44, 3, 'Confiando, confiando,\r\nSempre e só, Jesus, em Ti,\r\nTeu poder e Tua graça\r\nPodem bem guardar-me a mim.', 'Oh! Que glória! Oh! Que glória!\r\nGozo em meu coração!\r\nEu confio em Jesus,\r\nE, crendo, tenho salvação.\r\nOh! Que glória! Oh! Que glória!\r\nGozo em meu coração!\r\nEu confio em Jesus,\r\nE em Seu sangue achei perdão.', 1, NULL, NULL),
(44, 4, 'Consagrado ao Teu serviço,\r\nQuero eu pra Ti viver;\r\nDando sempre o testemunho\r\nDe Tua graça e Teu poder.', 'Oh! Que glória! Oh! Que glória!\r\nGozo em meu coração!\r\nEu confio em Jesus,\r\nE, crendo, tenho salvação.\r\nOh! Que glória! Oh! Que glória!\r\nGozo em meu coração!\r\nEu confio em Jesus,\r\nE em Seu sangue achei perdão.', 1, NULL, NULL),
(45, 1, 'Redentor onipotente,\r\nPoderoso Salvador,\r\nAdvogado onisciente\r\nÉ Jesus, meu bom Senhor.', 'Ó Amante da minh’alma,\r\nTu és tudo para mim!\r\nTudo quanto eu careço\r\nAcho, Jesus, só em Ti.', 1, NULL, NULL),
(45, 2, 'Um abrigo sempre perto,\r\nPara todo o pecador;\r\nUm refúgio sempre aberto,\r\nÉ Jesus. meu Salvador.', 'Ó Amante da minh’alma,\r\nTu és tudo para mim!\r\nTudo quanto eu careço\r\nAcho, Jesus, só em Ti.', 1, NULL, NULL),
(45, 3, 'Água viva! pão da vida!\r\nDoce sombra no calor,\r\nQue ao descanso nos convida.\r\nÉ Jesus, meu Salvador!', 'Ó Amante da minh’alma,\r\nTu és tudo para mim!\r\nTudo quanto eu careço\r\nAcho, Jesus, só em Ti.', 1, NULL, NULL),
(45, 4, 'Ó Cordeiro imaculado,\r\nQue Seu sangue derramou,\r\nMeus pecados expiando,\r\nA minh’alma resgatou.', 'Ó Amante da minh’alma,\r\nTu és tudo para mim!\r\nTudo quanto eu careço\r\nAcho, Jesus, só em Ti.', 1, NULL, NULL),
(45, 5, 'Fundamento inabalável!\r\nRocha firme e secular!\r\nInfalível! Imutável!\r\nQuem mo poderá tirar?', 'Ó Amante da minh’alma,\r\nTu és tudo para mim!\r\nTudo quanto eu careço\r\nAcho, Jesus, só em Ti.', 1, NULL, NULL),
(46, 6, 'O caminho que, seguro,\r\nSempre para o céu conduz,\r\nQuem a Cristo pronto segue,\r\nQuem tomar a sua cruz.', 'Ó Amante da minh’alma,\r\nTu és tudo para mim!\r\nTudo quanto eu careço\r\nAcho, Jesus, só em Ti.', 1, NULL, NULL);
INSERT INTO `ChristianHymns` (`Code`, `stanza_number`, `stanza_text`, `chorus_text01`, `chorus_repetition_number01`, `chorus_text02`, `chorus_repetition_number02`) VALUES
(47, 1, 'Rocha eterna, meu Jesus,\r\nQue, por mim, na amarga cruz,\r\nFoste morto eu meu lugar,\r\nMorto para me salvar;\r\nEm Ti quero me esconder,\r\nSó Tu podes me valer.', NULL, 0, NULL, NULL),
(47, 2, 'Minhas obras, eu bem sei,\r\nNada valem ante a lei;\r\nSe eu chorasse sem cessar.\r\nTrabalhasse sem cansar,\r\nTudo inútil, tudo em vão!\r\nSó em Ti há salvação.', NULL, 0, NULL, NULL),
(47, 3, 'Nada trago a Ti, Senhor!\r\n‘Spero só em Teu amor!\r\nTodo indigno e imundo sou,\r\nEis, sem Ti, perdido estou!\r\nNo Teu sangue, ó Salvador,\r\nLava um pobre pecador.', NULL, 0, NULL, NULL),
(47, 4, 'Quando a morte me chamar,\r\nE ante Ti me apresentar,\r\nRocha eterna, meu Jesus,\r\nQue por mim, na amarga cruz,\r\nFoste morto em meu lugar,\r\nQuero em Ti só me abrigar.', NULL, 0, NULL, NULL),
(48, 1, 'Quando lá do céu descendo,\r\npara os Seus, Jesus voltar,\r\nE o clarim de Deus a todos proclamar,\r\nQue chegou o grande dia do triunfar do meu Rei,\r\nEu, por Sua imensa graça, lá estarei.', 'Quando enfim, chegar o dia\r\nDo triunfar do meu Rei,\r\nQuando enfim, chegar o dia,\r\nPela graça de Jesus eu lá estarei!', 1, NULL, NULL),
(48, 2, 'Nesse dia, quando os mortos\r\nhão de a voz de Cristo ouvir,\r\nE dos seus sepulcros hão de ressurgir,\r\nOs remidos reunidos, logo aclamarão seu Rei,\r\nE, por Sua imensa graça, lá estarei.', 'Quando enfim, chegar o dia\r\nDo triunfar do meu Rei,\r\nQuando enfim, chegar o dia,\r\nPela graça de Jesus eu lá estarei!', 1, NULL, NULL),
(48, 3, 'Pelo mundo, rejeitado foi,\r\nJesus, meu Salvador.\r\nDesprezaram, insultaram meu Senhor,\r\nMas, faustoso, vem o dia\r\ndo triunfar do meu Rei,\r\nE, por Sua imensa graça, lá estarei.', 'Quando enfim, chegar o dia\r\nDo triunfar do meu Rei,\r\nQuando enfim, chegar o dia,\r\nPela graça de Jesus eu lá estarei!', 1, NULL, NULL),
(48, 4, 'Em mim mesmo, nada tenho em que possa confiar,\r\nMas Jesus morreu na cruz p’ra me salvar;\r\nTão somente nEle espero, sim, e sempre esperarei.\r\nPois, por Sua imensa graça, lá estarei.', 'Quando enfim, chegar o dia\r\nDo triunfar do meu Rei,\r\nQuando enfim, chegar o dia,\r\nPela graça de Jesus eu lá estarei!', 1, NULL, NULL),
(49, 1, 'Deus tem prometido a salvação dar.\r\nA quem em Seu Filho Jesus confiar.', 'Aleluia! Já creio em Cr!sto Jesus.\r\nE salvo já estou, pelo sangue da cruz!', 1, NULL, NULL),
(49, 2, 'Foi tal Seu amor, que me substituiu,\r\nE sobre o Calvário, minh’alma remiu!', 'Aleluia! Já creio em Cr!sto Jesus.\r\nE salvo já estou, pelo sangue da cruz!', 1, NULL, NULL),
(49, 3, 'Não posso jamais desse amor duvidar;\r\nNão posso senão aos Seus pés me prostrar.', 'Aleluia! Já creio em Cr!sto Jesus.\r\nE salvo já estou, pelo sangue da cruz!', 1, NULL, NULL),
(49, 4, 'E, quando, na glória, eu vir meu bom Rei,\r\nCom todos os santos ali cantarei.', 'Aleluia! Já creio em Cr!sto Jesus.\r\nE salvo já estou, pelo sangue da cruz!', 1, NULL, NULL),
(50, 1, 'Sempre fiéis, sim, a Ti, nós seremos,\r\nPor Tua graça, ó Cristo, Senhor!\r\nSempre fiéis, sim, por Ti lutaremos.\r\nSob Teu pendão, ó Jesus, Salvador!', 'Sempre fiéis, irmãos! Irmãos sejamos.\r\nSempre fiéis a Cristo Jesus.\r\nQue até à morte, por nós prosseguiu,\r\nE libertou-nos, morrendo na cruz!', 1, 'Sempre fiéis, sim, mesmo até à morte!\r\nSempre fiéis – Tomemos a cruz!\r\nEis a divisa que a nós nos pertence,\r\nAos libertados por Cristo Jesus!', 1),
(50, 2, 'Por Ti viver, ó bendito Cordeiro,\r\nQuem não deseja – se Te conhecer?\r\nQuem, que se diga cristão verdadeiro,\r\nPronto não ’steja, por Ti a sofrer?', 'Sempre fiéis, irmãos! Irmãos sejamos.\r\nSempre fiéis a Cristo Jesus.\r\nQue até à morte, por nós prosseguiu,\r\nE libertou-nos, morrendo na cruz!', 1, 'Sempre fiéis, sim, mesmo até à morte!\r\nSempre fiéis – Tomemos a cruz!\r\nEis a divisa que a nós nos pertence,\r\nAos libertados por Cristo Jesus!', 1),
(50, 3, 'Mas, Salvador, quão fraquinhos nós somos,\r\nComo podemos deixar de cair,\r\nSe por Ti mesmo guardados não formos?\r\nQuem, té à morte, Te pode seguir?', 'Sempre fiéis, irmãos! Irmãos sejamos.\r\nSempre fiéis a Cristo Jesus.\r\nQue até à morte, por nós prosseguiu,\r\nE libertou-nos, morrendo na cruz!', 1, 'Sempre fiéis, sim, mesmo até à morte!\r\nSempre fiéis – Tomemos a cruz!\r\nEis a divisa que a nós nos pertence,\r\nAos libertados por Cristo Jesus!', 1),
(51, 1, 'Ó minh’alma, sem demora,\r\nErgue-te para entoar,\r\nOs louvores do teu Cristo,\r\nE Seu nome celebrar!\r\nP’ra remir-te, p’ra remir-te,\r\nSua vida Ele quis dar!', NULL, 0, NULL, NULL),
(51, 2, 'Minha condição tão triste\r\nConheceu meu Salvador;\r\nQue dos céus desceu à terra,\r\nPara ser meu Redentor!\r\nOh! Quão grande! Oh! Quão grande!\r\nE o amor de meu Senhor!', NULL, 0, NULL, NULL),
(51, 3, 'Condenado, justamente,\r\nQue podia eu fazer,\r\nDessa pena p’ra livrar-me,\r\nO perdão p’ra merecer?\r\nO Seu sangue, o Seu sangue,\r\nQuis Jesus por mim verter!', NULL, 0, NULL, NULL),
(51, 4, 'Com meus crimes carregado,\r\nLá na cruz, em meu lugar,\r\nFoi Jesus crucificado,\r\nA minh’alma pra salvar;\r\nVinde todos! Vinde todos,\r\nJá comigo, O adorar!', NULL, 0, NULL, NULL),
(52, 1, 'Pelo amor de Deus bendito,\r\nVai tudo bem!\r\nSeu amor é infinito:\r\nTudo está bem!\r\nEsse amor nos tem provado.\r\nEm Seu Filho muito amado,\r\nQue, por nós, foi imolado.\r\nSim, sim, ‘stá bem!', NULL, 0, NULL, NULL),
(52, 2, 'Cristo, agora, o cetro empunha:\r\nVai tudo bem!\r\nQuem morreu é quem governa:\r\nTudo está bem!\r\nSeu amor é imutável,\r\nSeu poder inabalável,\r\nSeu cuidado é incansável,\r\nSim, sim, ‘stá bem!', NULL, 0, NULL, NULL),
(52, 3, 'A fé canta na tristeza:\r\nVai tudo bem!\r\nCanta, sim, e com firmeza:\r\nTudo está bem!\r\nPois se Deus é quem nos guia,\r\nTernamente nos vigia,\r\nCom bondade, noite e dia.\r\nSim, sim, ‘stá bem!', NULL, 0, NULL, NULL),
(52, 4, 'Por caminhos escabrosos,\r\nVai tudo bem!\r\nOu por mares tormentosos,\r\nTudo está bem!\r\nA Jesus tudo obedece;\r\nSempre o mesmo permanece!\r\nNem um só dos Seus esquece!\r\nSim, sim, ‘stá bem!', NULL, 0, NULL, NULL),
(52, 5, 'Quer na vida, quer na morte,\r\nVai tudo bem!\r\nQuão feliz é nossa sorte!\r\nTudo está bem!\r\nPelo sangue resgatados,\r\nE do mundo separados.\r\nSempre por Jesus guardados.\r\nSim, sim, ‘stá bem!', NULL, 0, NULL, NULL),
(53, 1, 'Até que volte o Salvador,\r\nCercando a mesa do Senhor,\r\nA Ceia vimos celebrar,\r\nDe Cristo, a morte anunciar,\r\nE com humilde devoção,\r\nRender a Deus adoração.', NULL, 0, NULL, NULL),
(53, 2, 'Até que volte o Salvador,\r\nAqui mostremos Seu amor;\r\nCom viva fá e gratidão,\r\nParticipemos deste pão,\r\nObedientes a Jesus,\r\nLembrando assim a Sua cruz!', NULL, 0, NULL, NULL),
(53, 3, 'Até que volte o Salvador,\r\nBebendo o cálice do Senhor,\r\nSeu nome queremos bendizer,\r\nE mais e mais engrandecer,\r\nO sangue que Ele derramou,\r\nO sangue que nos resgatou!', NULL, 0, NULL, NULL),
(53, 4, 'Se duras são as provações,\r\nSe fortes as perseguições,\r\nSe as lutas fazem-se sentir,\r\nE custa-nos as resistir,\r\nNão nos deixemos perturbar:\r\nSão só até Jesus voltar!', NULL, 0, NULL, NULL),
(53, 5, 'Bem prontos para O receber,\r\nDevemos sempre aqui viver;\r\nO tempo foge, o dia vem,\r\nA glória esperamos além,\r\nPois trabalhemos, com fervor,\r\nAté que volte o Salvador.', NULL, 0, NULL, NULL),
(54, 1, 'Minh’alma, louva ao Redentor\r\nJesus, teu Rei, teu Salvador,\r\nQue sobre a cruz, em teu lugar,\r\nSeu sangue deu p’ra te salvar!', 'Sim, sobre a cruz, em meu lugar,\r\nJesus morreu p’ra me salvar!\r\nSalvo e seguro nEle estou,\r\nPois com Seu sangue me livrou.', 1, NULL, NULL),
(54, 2, 'Oh! quão perdido e longe andei,\r\nLonge de Deus e Sua lei!\r\nCom terno e paciente amor\r\nSeguiu-me sempre, o Salvador!', 'Sim, sobre a cruz, em meu lugar,\r\nJesus morreu p’ra me salvar!\r\nSalvo e seguro nEle estou,\r\nPois com Seu sangue me livrou.', 1, NULL, NULL),
(54, 3, 'Bendito dia, quando, enfim,\r\nVi Cristo sobre a cruz, por mim!\r\nVencido pelo Seu amor,\r\nRendi-me logo ao Salvador.', 'Sim, sobre a cruz, em meu lugar,\r\nJesus morreu p’ra me salvar!\r\nSalvo e seguro nEle estou,\r\nPois com Seu sangue me livrou.', 1, NULL, NULL),
(54, 4, 'Vem, alma aflita, descansar;\r\nEis Cristo pronto a perdoar!\r\nConfia nEle, de coração,\r\nE teu será o gran perdão.', 'Sim, sobre a cruz, em meu lugar,\r\nJesus morreu p’ra me salvar!\r\nSalvo e seguro nEle estou,\r\nPois com Seu sangue me livrou.', 1, NULL, NULL),
(55, 1, 'Meu Senhor, sou Teu – Tua voz ouvi\r\nA chamar-me, com amor;\r\nMais, de Ti, mais perto eu quero estar,\r\nÓ bendito Salvador!', 'Mais perto, perto da Tua cruz\r\nQuero estar, ó Salvador!\r\nMais perto, perto, perto da Tua cruz\r\nLeva-me, ó meu Senhor!', 1, NULL, NULL),
(55, 2, 'A seguir-Te só, me consagro eu,\r\nConstrangido pelo amor,\r\nE, alegre, já me declaro Teu,\r\nP’ra servir-Te a Ti, Senhor.', 'Mais perto, perto da Tua cruz\r\nQuero estar, ó Salvador!\r\nMais perto, perto, perto da Tua cruz\r\nLeva-me, ó meu Senhor!', 1, NULL, NULL),
(55, 3, 'Oh! Que pura e santa delicie é,\r\nAos Teus santos pés me achar,\r\nE com viva e reverente fé,\r\nCom meu Salvador falar.', 'Mais perto, perto da Tua cruz\r\nQuero estar, ó Salvador!\r\nMais perto, perto, perto da Tua cruz\r\nLeva-me, ó meu Senhor!', 1, NULL, NULL),
(56, 1, 'Dantes, trabalhava sempre com temor,\r\nMas descanso, agora em meu Salvador;\r\nDantes “esperava”; hoje, “bem o sei”\r\nQue estou salvo em Cristo, meu bendito Rei.', 'Tudo! Tudo em Custo’\r\nQue por mim morreu.\r\nTudo! Tudo em Cristo!\r\nCristo é todo meu.', 1, NULL, NULL),
(56, 2, 'Dantes, desejava bênção do Senhor;\r\nHoje, mais de Cristo, mais de Seu amor!\r\nNão somente a bênção, que tão pronto dá,\r\nMas Ele mesmo a fonte em quem tudo está.', 'Tudo! Tudo em Custo’\r\nQue por mim morreu.\r\nTudo! Tudo em Cristo!\r\nCristo é todo meu.', 1, NULL, NULL),
(56, 3, 'Dantes, duvidava, era sempre “o eu”:\r\nHoje, bem conheço Cristo, além do véu,\r\nSacerdote eterno, lá por mim entrou;\r\nNEle estou completo, nEle aceito sou.', 'Tudo! Tudo em Custo’\r\nQue por mim morreu.\r\nTudo! Tudo em Cristo!\r\nCristo é todo meu.', 1, NULL, NULL),
(56, 4, 'Dantes queria o mundo; hoje, é só Jesus!\r\nDantes, eram trevas: hoje, é plena luz;\r\nDantes, o receio, hoje, a doce paz,\r\nTudo a Cristo deixo, Ele me satisfaz.', 'Tudo! Tudo em Custo’\r\nQue por mim morreu.\r\nTudo! Tudo em Cristo!\r\nCristo é todo meu.', 1, NULL, NULL),
(57, 1, 'Deus presente está conosco.\r\nPronto todos a salvar;\r\nSobre as almas sequiosas,\r\nQuer Sua bênção derramar.', 'Manda, ó manda as ricas chuvas\r\nDa Tua bênção, Salvador!\r\nImploramos! Esperamos!\r\nViviflca-nos, Senhor!', 1, NULL, NULL),
(57, 2, 'Eis a Ti, Senhor, erguemos\r\nNossos pobres corações;\r\nNa Tua grande e rica graça,\r\nOuve as nossas petições!', 'Manda, ó manda as ricas chuvas\r\nDa Tua bênção, Salvador!\r\nImploramos! Esperamos!\r\nViviflca-nos, Senhor!', 1, NULL, NULL),
(57, 3, 'Torna a nossa fé mais viva,\r\nMais ardente o nosso amor;\r\nEnche-nos de santo zelo,\r\nDe coragem e fervor.', 'Manda, ó manda as ricas chuvas\r\nDa Tua bênção, Salvador!\r\nImploramos! Esperamos!\r\nViviflca-nos, Senhor!', 1, NULL, NULL),
(58, 1, '“Não temas! Contigo, Eu sempre estarei!\r\nOh! rica promessa do bondoso Rei;\r\nQual estrela que brilha, lá na escuridão,\r\nEsta linda promessa brilha no meu coração.', 'Comigo estar! Comigo estar!\r\nSim, Jesus me promete,\r\nSempre comigo estar.\r\nComigo estar! Comigo estar!\r\nSim, Jesus me promete,\r\nSempre comigo estar.', 1, NULL, NULL),
(58, 2, 'Os lírios mais alvos, ei-los murchos estão!\r\nOs dias mais belos, quão depressa vão!\r\nCristo, o lírio dos vales, nunca mudará:\r\nCristo, a luz celeste, sempre comigo estará!', 'Comigo estar! Comigo estar!\r\nSim, Jesus me promete,\r\nSempre comigo estar.\r\nComigo estar! Comigo estar!\r\nSim, Jesus me promete,\r\nSempre comigo estar.', 1, NULL, NULL),
(58, 3, 'E, se pelas águas tiver de passar,\r\nSeus braços eternos hão de me guardar;\r\nSim, mesmo no fogo. que vem me provar,\r\nMeu Senhor me promete sempre comigo estar!', 'Comigo estar! Comigo estar!\r\nSim, Jesus me promete,\r\nSempre comigo estar.\r\nComigo estar! Comigo estar!\r\nSim, Jesus me promete,\r\nSempre comigo estar.', 1, NULL, NULL),
(59, 1, 'Te conhecer o amor sem fim,\r\nUm pecador fui eu;\r\nEm o meu pensar não houve lugar\r\nPara Cristo e o céu.', 'Eu creio, sim, Já creio, sim,\r\nJesus morreu por mim;\r\nPelo sangue Seu, que Ele verteu,\r\nLibertado fui por fim.', 1, NULL, NULL),
(59, 2, 'Pois, quando vi Jesus por mim,\r\nSofrendo sobre a cruz,\r\nO meu coração, sem hesitação,\r\nRecebeu o amor e a luz.', 'Eu creio, sim, Já creio, sim,\r\nJesus morreu por mim;\r\nPelo sangue Seu, que Ele verteu,\r\nLibertado fui por fim.', 1, NULL, NULL),
(59, 3, 'Com lágrimas, pedi perdão,\r\nE dor, também senti;\r\nVeio, então, Jesus, e a mim falou:\r\n“Foi por ti que Eu morri”.', 'Eu creio, sim, Já creio, sim,\r\nJesus morreu por mim;\r\nPelo sangue Seu, que Ele verteu,\r\nLibertado fui por fim.', 1, NULL, NULL),
(59, 4, 'Eu sei que Deus, no Filho Seu,\r\nMe vê perfeito e são;\r\nNão há mais temor, só bendito amor,\r\nGozo no meu coração.', 'Eu creio, sim, Já creio, sim,\r\nJesus morreu por mim;\r\nPelo sangue Seu, que Ele verteu,\r\nLibertado fui por fim.', 1, NULL, NULL),
(60, 1, 'Vinde, cantai e entoei\r\nLouvores a Jesus,\r\nQue, para nossa salvação,\r\nMorreu na amarga cruz,\r\nSeu sangue derramou, de tudo me lavou,\r\nMais alvo do que a neve me tornou.', 'O sangue de Jesus me lavou, me lavou!\r\nO sangue de Jesus me lavou, me lavou!\r\nAlegre cantarei louvores ao meu Rei,\r\nA meu Senhor Jesus, que me salvou.', 1, NULL, NULL),
(60, 2, 'Vinde conosco vos unir\r\nNa guerra contra o mal;\r\nE com nosso Salvador,\r\nEm marcha triunfal,\r\nA todos proclamar, Sua graça e Seu poder.\r\nSeu sangue derramou p’ra nos salvar!', 'O sangue de Jesus me lavou, me lavou!\r\nO sangue de Jesus me lavou, me lavou!\r\nAlegre cantarei louvores ao meu Rei,\r\nA meu Senhor Jesus, que me salvou.', 1, NULL, NULL),
(60, 3, 'O Capitão da salvação\r\nÉ Cristo, o Salvador;\r\nO Rei dos reis, o Redentor.\r\nJesus, nosso Senhor;\r\nEle tudo vencerá, vitória nos dará;\r\nA glória salvos, nos conduzirá.', 'O sangue de Jesus me lavou, me lavou!\r\nO sangue de Jesus me lavou, me lavou!\r\nAlegre cantarei louvores ao meu Rei,\r\nA meu Senhor Jesus, que me salvou.', 1, NULL, NULL),
(61, 1, 'Em todo o tempo, irmão, o Senhor\r\nTomará conta de ti.\r\nCristo, que fala de vida e amor,\r\nTomará conta de ti.', 'Deus tomará conta de ti,\r\nDeus tomará conta de ti,\r\nNEle descansa, sempre aqui,\r\nPois tem cuidado Deus, de ti.', 1, NULL, NULL),
(61, 2, 'Em toda prova, irmão, o Senhor\r\nTomará conta de ti\r\nCristo, que é teu amado Pastor,\r\nTomará conta de ti.', 'Deus tomará conta de ti,\r\nDeus tomará conta de ti,\r\nNEle descansa, sempre aqui,\r\nPois tem cuidado Deus, de ti.', 1, NULL, NULL),
(61, 3, 'Em toda parte, irmão, o Senhor\r\nTomará conta de ti.\r\nCristo, que nos dá poder e valor,\r\nTomará conta de ti.', 'Deus tomará conta de ti,\r\nDeus tomará conta de ti,\r\nNEle descansa, sempre aqui,\r\nPois tem cuidado Deus, de ti.', 1, NULL, NULL),
(61, 4, 'Chegando a morte, irmão, o Senhor\r\nTomará conta de ti.\r\nCristo será teu fiel condutor:\r\nTomará conta de ti.', 'Deus tomará conta de ti,\r\nDeus tomará conta de ti,\r\nNEle descansa, sempre aqui,\r\nPois tem cuidado Deus, de ti.', 1, NULL, NULL),
(62, 1, 'Achei Jesus, achei Jesus,\r\nOh! gozo incomparável!\r\nPois salvo estou, já tenho luz\r\nE paz inexplicável!\r\nOs meus pecados expiou\r\nO sangue do Cordeiro;\r\nAs minhas trevas dissipou;\r\nDe Deus eu sou herdeiro!', NULL, 0, NULL, NULL),
(62, 2, 'Jesus mui longe me buscou,\r\nPerdido me encontrava!\r\nE do pecado me salvou,\r\nO qual me atormentava,\r\nPertenço ao meu bom Redentor,\r\nAo meu melhor amigo,\r\nQue me livrou de toda a dor,\r\nE sempre está comigo.', NULL, 0, NULL, NULL),
(62, 3, 'Oh! Que prazer, oh! Que prazer!\r\nJesus é meu amado!\r\nDo Salvador eu quero ser\r\nUm servo consagrado;\r\nPois já me deu a salvação,\r\nQue nos foi prometida,\r\nAo meu cansado coração\r\nEncheu de nova vida.', NULL, 0, NULL, NULL),
(62, 4, 'Desejo a meu Jesus louvar,\r\nEm doce harmonia;\r\nEm Cristo, tudo fui achar,\r\nPois tudo carecia;\r\nE sem cessar, o Salvador,\r\nMe ama ternamente;\r\nO que direi a tal amor?\r\n“Te amo eternamente!”', NULL, 0, NULL, NULL),
(63, 1, 'Eis marchamos para aquele bom país,\r\nOnde o crente (sim, é Cristo quem o diz),\r\nCom seu Salvador pra sempre ali feliz,\r\nVai com Ele descansar.\r\nTrabalhemos, pois, com zelo e com vigor,\r\nConstrangidos pelo Seu imenso amor;\r\nTrabalhemos pelo nosso Salvador:\r\nEis que a vida vai findar!', 'Acordai! Acordai! Despertai! Despertai!\r\nE cantai! Sim, cantai! O Senhor não tardará.\r\nEis marchamos para aquele bom país,\r\nOnde o crente (sim, é Cristo quem o diz),\r\nCom seu Salvador, p’ra sempre ali feliz,\r\nVai com Ele descansar.', 1, NULL, NULL),
(63, 2, 'Eis conosco nosso insigne Capitão,\r\nQue nos assegura eterna salvação!\r\nEis da santa fé, o invicto pavilhão!\r\nVamos, vamos trabalhar!\r\nEis avante! Nada temos que temer;\r\nPor Jesus, havemos sempre de vencer,\r\nTrabalhemos, pois até o amanhecer,\r\nE o trabalho aqui findar!', 'Acordai! Acordai! Despertai! Despertai!\r\nE cantai! Sim, cantai! O Senhor não tardará.\r\nEis marchamos para aquele bom país,\r\nOnde o crente (sim, é Cristo quem o diz),\r\nCom seu Salvador, p’ra sempre ali feliz,\r\nVai com Ele descansar.', 1, NULL, NULL),
(63, 3, 'Revestidos da couraça de Jesus,\r\nComo servos Seus e filhos, sim, da luz,\r\nGloriando-nos em Cristo e Sua cruz!\r\nVamos, vamos, trabalhar!\r\nOs perdidos, vamos com amor buscar,\r\nAos desesperados, vamos declarar\r\nQue Jesus ‘stá pronto todos a salvar!\r\nÓ sim, vamos trabalhar!', 'Acordai! Acordai! Despertai! Despertai!\r\nE cantai! Sim, cantai! O Senhor não tardará.\r\nEis marchamos para aquele bom país,\r\nOnde o crente (sim, é Cristo quem o diz),\r\nCom seu Salvador, p’ra sempre ali feliz,\r\nVai com Ele descansar.', 1, NULL, NULL),
(64, 1, 'De todo o mundo, eis-nos aqui\r\nEm Ti só confiando,\r\nPermanecendo em Teu amor,\r\nTeu reino só buscando,\r\nAs nossas vidas, nosso amor,\r\nA Ti trazemos, Salvador,\r\nCantando: “Aleluia!”', NULL, 0, NULL, NULL),
(64, 2, 'Viver por Ti, em Ti morrer,\r\nOs nossos pais miravam;\r\nA Tua vontade só fazer,\r\nEis o que desejavam.\r\nE nós, que somos Teus, também\r\nDizemos, firmes: Sim, Amém’\r\nE: “Glória! Aleluia!”', NULL, 0, NULL, NULL),
(64, 3, 'Ao Evangelho salvador,\r\nTão puro e glorioso,\r\nAs leis santíssimas que Tu\r\nNos dás, Senhor bondoso,\r\n“Amém! Dizemos com fervor,\r\nE, pelo Teu imenso amor,\r\nClamamos: “Aleluia!”', NULL, 0, NULL, NULL),
(64, 4, 'Sim, constrangidos pelo amor,\r\nPerdidos procuramos\r\nSalvar, – ganhar p’ra Ti, Senhor:\r\nEis o que ambicionamos,\r\nSeguindo sempre a Tua lei,\r\nA Ti, leais, bendito Rei,\r\nCantamos: “Aleluia!”', NULL, 0, NULL, NULL),
(64, 5, 'Nem o labor será em vão,\r\nPois eis, que glorioso,\r\nO dia vem, em que Jesus\r\nVirá vitorioso;\r\nE todo mundo cantará,\r\nE jubiloso O exaltará,\r\nCom: “Amém”, “Aleluia!”', NULL, 0, NULL, NULL),
(65, 1, 'Eis os milhões, que em trevas tão medonhas\r\nJazem perdidos, sem o Salvador!\r\nQuem, quem irá as novas proclamando,\r\nQue Deus, em Cristo, salva o pecador?', '“Todo o poder o Pai me deu,\r\nNa terra, como lá no céu!\r\nIde, pois, anunciar o Evangelho,\r\nE eis-me convosco Sempre!”', 1, NULL, NULL),
(65, 2, 'Portas abertas, eis por todo o mundo!\r\nCristãos, erguei-vos! Já avante anda!!\r\nCrentes em Cristo! Uni as vossas forças.\r\nDa escravidão os povos liberta!.', '“Todo o poder o Pai me deu,\r\nNa terra, como lá no céu!\r\nIde, pois, anunciar o Evangelho,\r\nE eis-me convosco Sempre!”', 1, NULL, NULL),
(65, 3, '“Ó vinde a mim! A voz divina clama,\r\n“Vinde!” Clama! em nome de Jesus:\r\nPra nos salvar da maldição eterna,\r\nSeu sangue derramou por nós na cruz.', '“Todo o poder o Pai me deu,\r\nNa terra, como lá no céu!\r\nIde, pois, anunciar o Evangelho,\r\nE eis-me convosco Sempre!”', 1, NULL, NULL),
(65, 4, 'Ó Deus apressa o dia glorioso,\r\nEm que os remidos todos se unirão,\r\nE, em coro excelso, santo, jubiloso,\r\nP’ra todo o sempre, glória a Ti darão.', '“Todo o poder o Pai me deu,\r\nNa terra, como lá no céu!\r\nIde, pois, anunciar o Evangelho,\r\nE eis-me convosco Sempre!”', 1, NULL, NULL),
(66, 1, 'Todo o que crê no Filho de Deus,\r\nQue, p’ra nos remir, desceu dos céus;\r\nTodo o que, contrito, a Deus se chegar,\r\nJesus está pronto, agora, a salvar.', 'Sim, Jesus te chama, ó meu Irmão!\r\nA ti Ele oferece pleno perdão;\r\nSeu sangue verteu para te resgatar,\r\nE Ele é quem quer e pode salvar.', 1, NULL, NULL),
(66, 2, 'Quem, arrependido, busca perdão,\r\nAbrindo a Jesus o seu coração,\r\nA paz hoje mesmo pode gozar,\r\nPois Ele está pronto, agora, a salvar.', 'Sim, Jesus te chama, ó meu Irmão!\r\nA ti Ele oferece pleno perdão;\r\nSeu sangue verteu para te resgatar,\r\nE Ele é quem quer e pode salvar.', 1, NULL, NULL),
(67, 1, 'Quem quer hoje mesmo a Jesus seguir?\r\nSeu pendão alçando, ao combate ir?\r\nQuem quer, humilhado, sempre O servir,\r\nE obedecê-Lo. Seu querer cumprir?', 'Quem quer ir com Cristo, quem responderá,\r\nPresto, sem demora, “eu, Senhor, irei”?\r\nOnde for o Mestre, tu também irás?\r\nQuem dirá agora: “eu Te seguirei”?', 1, NULL, NULL),
(67, 2, 'Quem seguir a Cristo quer, com mui amor,\r\nDando-Lhe tributo, dando-Lhe louvor;\r\nDe Sua nobre causa, sendo defensor,\r\nE na santa vinha, ser trabalhador?', 'Quem quer ir com Cristo, quem responderá,\r\nPresto, sem demora, “eu, Senhor, irei”?\r\nOnde for o Mestre, tu também irás?\r\nQuem dirá agora: “eu Te seguirei”?', 1, NULL, NULL),
(67, 3, 'Queres tu segui-Lo, sem vacilação,\r\nE fugir dos braços da vil tentação?\r\nSempre confiando na Sua proteção,\r\nPor fé desfrutando Sua salvação.', 'Quem quer ir com Cristo, quem responderá,\r\nPresto, sem demora, “eu, Senhor, irei”?\r\nOnde for o Mestre, tu também irás?\r\nQuem dirá agora: “eu Te seguirei”?', 1, NULL, NULL),
(68, 1, 'Podes receber a doce paz de Deus,\r\nCrendo em Jesus, que já desceu dos céus:\r\nToma, hoje, tua cruz,\r\nVem das trevas para a luz,\r\nE, assim, mui gozo te dará Jesus!', 'Gozo de ter salvação,\r\nGozo no teu coração.\r\nAlegria fruirás,\r\nQuando desfrutares paz,\r\nQue te trouxa Cristo, na cruenta cruz!', 1, NULL, NULL),
(68, 2, 'O amor de Cristo podes conhecer,\r\nSeu sustento santo podes receber:\r\nNão mais só combaterás,\r\nCristo te defenderá,\r\nE, assim, mui gozo te dará Jesus.', 'Gozo de ter salvação,\r\nGozo no teu coração.\r\nAlegria fruirás,\r\nQuando desfrutares paz,\r\nQue te trouxa Cristo, na cruenta cruz!', 1, NULL, NULL),
(68, 3, 'Queres tu, de Cristo, fiel soldado ser?\r\nPelejar por Ele sempre sem temer?\r\nVem, então, ao Salvador;\r\nFar-te-á mui vencedor,\r\nE, assim, mui gozo te dará Jesus.', 'Gozo de ter salvação,\r\nGozo no teu coração.\r\nAlegria fruirás,\r\nQuando desfrutares paz,\r\nQue te trouxa Cristo, na cruenta cruz!', 1, NULL, NULL),
(68, 4, 'Poderás por Cristo ser aqui uma luz,\r\nSe obedeceres ao Senhor Jesus!\r\nÓ vem, hoje, sem temor,\r\nAceitar o Salvador,\r\nE, assim, mui gozo te dará Jesus.', 'Gozo de ter salvação,\r\nGozo no teu coração.\r\nAlegria fruirás,\r\nQuando desfrutares paz,\r\nQue te trouxa Cristo, na cruenta cruz!', 1, NULL, NULL),
(69, 1, 'Jesus quebrou os meus grilhões,\r\nCom sangue me comprou;\r\nA minha alma, de prisões,\r\nEterna resgatou.', 'Aleluia! Cristo salva o perdido pecador!\r\nAleluia! Cristo salva o perdido com amor!', 1, NULL, NULL),
(69, 2, 'É minha paz, e proteção,\r\nMeu bom Consolador;\r\nAuxílio certo na aflição,\r\nRemédio para a dor,', 'Aleluia! Cristo salva o perdido pecador!\r\nAleluia! Cristo salva o perdido com amor!', 1, NULL, NULL),
(69, 3, 'Nenhum perigo temerei,\r\nEstando em Jesus;\r\nAs dores não mais sofrerei;\r\nJesus é minha luz.', 'Aleluia! Cristo salva o perdido pecador!\r\nAleluia! Cristo salva o perdido com amor!', 1, NULL, NULL),
(69, 4, 'Jesus, Teu nome louvarei,\r\nCom todo o coração;\r\nAlegre ao mundo anunciarei\r\nTão grande salvação.', 'Aleluia! Cristo salva o perdido pecador!\r\nAleluia! Cristo salva o perdido com amor!', 1, NULL, NULL),
(70, 1, 'Nós Aleluia daremos a Cristo,\r\nQuando O virmos nas nuvens voltar,\r\nDe esplendor e de glória vestido,\r\nSeus escolhidos vai arrebatar.', 'Cristo Jesus logo vai voltar,\r\nVai voltar, vai voltar,\r\nSeu povo vem, sim, arrebatar;\r\nJesus breve regressará!', 1, NULL, NULL),
(70, 2, 'Eis que estou no Senhor confiando,\r\nTodo o meu ser a Jesus entreguei.\r\nTodos prazeres eu tenho deixado,\r\nEm Jesus Cristo meu gozo achei.', 'Cristo Jesus logo vai voltar,\r\nVai voltar, vai voltar,\r\nSeu povo vem, sim, arrebatar;\r\nJesus breve regressará!', 1, NULL, NULL),
(70, 3, 'Como há de ser para ti Sua vinda?\r\nEla trará gozo ao teu coração?\r\nOu te fará padecer mais ainda?\r\nTemes que só te trará maldição?', 'Cristo Jesus logo vai voltar,\r\nVai voltar, vai voltar,\r\nSeu povo vem, sim, arrebatar;\r\nJesus breve regressará!', 1, NULL, NULL),
(70, 4, 'Cristo a mim a promessa tem dado\r\nQue junto dEle, também reinarei;\r\nBreve será meu Jesus proclamado\r\nSobre a terra, por todos: “O Rei”.', 'Cristo Jesus logo vai voltar,\r\nVai voltar, vai voltar,\r\nSeu povo vem, sim, arrebatar;\r\nJesus breve regressará!', 1, NULL, NULL),
(71, 1, 'Santo! Santo! Grande eterno Deus,\r\nCom alegria nós Te louvamos.\r\nRei dos reis e Criador dos céus.\r\nHumilhados Te adoramos;\r\nHonra e glória em Ti miramos,\r\nPois foi rasgado, na cruz, o véu.', 'Santo para sempre és Tu, Senhor\r\nNas batalhas és vencedor!', NULL, NULL, NULL),
(71, 2, 'Adorai-O terra, céu e mar;\r\nToda a Igreja, seus mensageiros,\r\nReunidos, vinde suplicar\r\nDons celestes, pois sois herdeiros\r\nDe Deus, e com Jesus coherdeiros\r\nDa glória que jamais vai findar.', 'Santo para sempre és Tu, Senhor\r\nNas batalhas és vencedor!', NULL, NULL, NULL),
(71, 3, 'Rei doa séc’los, grande eterno Deus!\r\nTu és veraz e incompreensível,\r\nCriador da terra e dos céus;\r\nÉs o Ancião invisível,\r\nHabitas na luz inacessível,\r\nLá morarão os remidos Teus.', 'Santo para sempre és Tu, Senhor\r\nNas batalhas és vencedor!', NULL, NULL, NULL),
(72, 1, 'Rasgou-se o véu, franca ficou a entrada:\r\nConfiança temos, para ali entrar,\r\nOnde a divina glória revelada,\r\nOutrora, não podíamos mirar;\r\nPor Cristo, entrando, nada ali tememos;\r\nA glória brilha, sem nos ofuscar;\r\nNa luz estamos e permanecemos\r\nFirmes, tranqüilos, sem mais vacilar.', NULL, 0, NULL, NULL),
(72, 2, 'Contigo ali, Senhor, nos encontramos,\r\nPois nos levaste já ao santo Pai,\r\nEm cuja graça aceitos, sempre estamos,\r\nEm cujo amor tão grande nos atrai\r\nA Ti, ó Filho – em Ti, ó bem amado!\r\nE só em Ti temos aceitação;\r\nE quem, no sangue estando já lavado,\r\nIsento está da eterna perdição.		', NULL, 0, NULL, NULL),
(72, 3, 'Ó Deus, a Ti de coração louvamos,\r\nPois de Ti nos quiseste aproximar,\r\nQuando separados de Ti ficamos,\r\nPor Tua lei divina, violar,\r\nTeus santos seguidores, consagrados,\r\nNo sangue do bendito Salvador,\r\nO culto e adoração Te apresentamos,\r\nPorque nos chamaste, ó bom Senhor!', NULL, 0, NULL, NULL),
(73, 1, 'Pecador escuta a voz do Senhor:\r\n“Se estás cansado, ó vem descansar;\r\nVem, não te detenhas; sem nenhum temor,\r\nDeixa o teu fardo e vem repousar”.', 'Vem, vem a Mim e descansarás;\r\nToma o Meu Jugo e Te guiarei;\r\nSou humilde, manso, dou-Te minha paz;\r\nÓ vem, hoJe mesmo, e Te salvarei.', 1, NULL, NULL),
(73, 2, 'Vem a Deus, faminto, pobre mesmo assim;\r\nHá maná celeste, preparado já;\r\nGrátis é a festa, p’ra ti e p’ra mim,\r\nCristo te convida, vem e cearás.', 'Vem, vem a Mim e descansarás;\r\nToma o Meu Jugo e Te guiarei;\r\nSou humilde, manso, dou-Te minha paz;\r\nÓ vem, hoJe mesmo, e Te salvarei.', 1, NULL, NULL),
(73, 3, 'Andas desviado, longe do redil?\r\nDeixa de vagar e vem ao bom Pastor;\r\nPelos montes, vales, há perigos mil?\r\nVolta ao rebanho do teu Salvador.', 'Vem, vem a Mim e descansarás;\r\nToma o Meu Jugo e Te guiarei;\r\nSou humilde, manso, dou-Te minha paz;\r\nÓ vem, hoJe mesmo, e Te salvarei.', 1, NULL, NULL),
(73, 4, 'Crendo tu, em Cristo, salvação terás;\r\nGozo, paz perfeita, tudo que é mister;\r\nCristo não despreza, nem rejeitará\r\nTodo o que contrito a Ele vier.', 'Vem, vem a Mim e descansarás;\r\nToma o Meu Jugo e Te guiarei;\r\nSou humilde, manso, dou-Te minha paz;\r\nÓ vem, hoJe mesmo, e Te salvarei.', 1, NULL, NULL),
(74, 1, 'Talvez Cristo venha ao romper da aurora,\r\nCom santos arcanjos, e com voz sonora;\r\nOs mortos porá dos sepulcros p’ra fora;\r\nJesus, breve, vem nos buscar.', 'Cristo, que há de vir, virá!\r\nEle não tardara, sim, Jesus vem;\r\nAleluia! Aleluia! Amém! Aleluia! Amém!', 1, NULL, NULL),
(74, 2, 'Talvez voltará quando o dia feneça,\r\nOu em uma noite a luz resplandeça;\r\nIrmãos, esperai que Jesus apareça!\r\nJesus, breve, vem nos buscar.', 'Cristo, que há de vir, virá!\r\nEle não tardara, sim, Jesus vem;\r\nAleluia! Aleluia! Amém! Aleluia! Amém!', 1, NULL, NULL),
(74, 3, 'O Seu esplendor e a glória veremos,\r\nDo mundo, então, nós por fim, sairemos,\r\nAssim, grande gozo no céu fruiremos;\r\nJesus, breve, vem nos buscar.', 'Cristo, que há de vir, virá!\r\nEle não tardara, sim, Jesus vem;\r\nAleluia! Aleluia! Amém! Aleluia! Amém!', 1, NULL, NULL),
(75, 3, 'Sê manso e benigno, qual morto até.\r\nNa rocha eterna, firma tua fé;\r\nVeraz é teu dito: de Deus é teu ser?\r\nT’espera a coroa, tu podes vencer.', 'Em Jesus tens a palma\r\nDa vitória, minh’alma;\r\nE também doce calma\r\nPelo sangue da cruz!', 1, NULL, NULL),
(75, 2, 'Evita o pecado, procura agradar\r\nA Deus, a quem deves no corpo exaltar;\r\nNão manches teus lábios com impura voz;\r\nDefende tua alma do vicio atroz.', 'Em Jesus tens a palma\r\nDa vitória, minh’alma;\r\nE também doce calma\r\nPelo sangue da cruz!', 1, NULL, NULL),
(75, 1, 'Tentado, não cedas, ceder é pecar,\r\nMelhor e mais nobre, será triunfar,\r\nCoragem, ó crente, domina o teu mal.\r\nDeus pode livrar-te de queda fatal.', 'Em Jesus tens a palma\r\nDa vitória, minh’alma;\r\nE também doce calma\r\nPelo sangue da cruz!', 1, NULL, NULL),
(76, 1, 'Vem, ó pródigo, vem sem tardar, te chama Deus,\r\nOuve-O chamando, sim, chamando a ti;\r\nTu, que vagas errante escuta a voz dos céus,\r\nÓ escuta a voz de amor.', 'Deus espera por ti. perdido, pródigo, vem;\r\nDeus espera por ti, perdido, pródigo, vem', 1, NULL, NULL),
(76, 2, 'Com paciência e ternura te chama o Senhor,\r\nOuve-O chamando, sim, chamando a ti;\r\nVem enquanto te chama o meigo Salvador,\r\nÓ escuta a voz de amor!', 'Deus espera por ti. perdido, pródigo, vem;\r\nDeus espera por ti, perdido, pródigo, vem', 1, NULL, NULL),
(76, 3, 'Lá na casa do Pai, abundância há de pão;\r\nOuve-O chamando, sim, chamando a ti;\r\nJá está pronta a mesa com toda a provisão,\r\nÓ escuta a voz de amor!', 'Deus espera por ti. perdido, pródigo, vem;\r\nDeus espera por ti, perdido, pródigo, vem', 1, NULL, NULL),
(77, 1, 'Queres, neste mundo, ser um vencedor?\r\nQueres tu cantar nas lutas e na dor?\r\nQueres ser alegre, qual bom lutador?\r\nGuarda o contacto com teu Salvador!', 'Guarda o contacto com teu Salvador,\r\nE a nuvem do mal não te cobrirá;\r\nPela senda alegre, tu caminharás\r\nIndo em contacto com teu Salvador.', 1, NULL, NULL),
(77, 2, 'Neste mundo, vivem muitos a penar,\r\nCujos corações transbordam de pesar;\r\nDá-lhes a mensagem de amor sem par;\r\nCom Deus o contacto deves tu guardar!', 'Guarda o contacto com teu Salvador,\r\nE a nuvem do mal não te cobrirá;\r\nPela senda alegre, tu caminharás\r\nIndo em contacto com teu Salvador.', 1, NULL, NULL),
(77, 3, 'Queres tu, com Deus, a comunhão obter?\r\nSua glória em ti sempre permanecer?\r\nQue o mundo possa Cristo em ti ver!\r\nGuarda o contacto co’o supremo Ser.', 'Guarda o contacto com teu Salvador,\r\nE a nuvem do mal não te cobrirá;\r\nPela senda alegre, tu caminharás\r\nIndo em contacto com teu Salvador.', 1, NULL, NULL),
(77, 4, 'Deixa que o Espírito implante em teu ser,\r\nO amor de Cristo, divinal prazer;\r\nQueres, neste mundo, todo mal vencer?\r\nGuarda o contacto e terás poder!', 'Guarda o contacto com teu Salvador,\r\nE a nuvem do mal não te cobrirá;\r\nPela senda alegre, tu caminharás\r\nIndo em contacto com teu Salvador.', 1, NULL, NULL),
(78, 1, 'Vagando eu em grande escuridão,\r\nO Salvador me encontrou,\r\nPerdido, mesmo, sem ter salvação;\r\nA minh’alma Seu sangue lavou.', 'Jesus é o meu forte Redentor;\r\nEle é meu grande Salvador,\r\nQue me libertou na cruz, com mui amor,\r\nCristo é meu Rei e meu Senhor.', 1, NULL, NULL),
(78, 2, 'Nos meus pecados e vil turbação,\r\nA luz mostrou-me, o Senhor;\r\nE fez-me ver que ali na perdição\r\nMe alcançava Seu grande amor.', 'Jesus é o meu forte Redentor;\r\nEle é meu grande Salvador,\r\nQue me libertou na cruz, com mui amor,\r\nCristo é meu Rei e meu Senhor.', 1, NULL, NULL),
(78, 3, 'Eu gozo desta grande salvação,\r\nMeu nome já no céu está;\r\nE recebi a divina! unção,\r\nQue aos fiéis ainda Deus dá.', 'Jesus é o meu forte Redentor;\r\nEle é meu grande Salvador,\r\nQue me libertou na cruz, com mui amor,\r\nCristo é meu Rei e meu Senhor.', 1, NULL, NULL),
(78, 4, 'Num dia alegre, Cristo voltará,\r\nE para o céu eu subirei;\r\nMeu corpo transformado, então será,\r\nQual o de Cristo Jesus. o meu Rei.', 'Jesus é o meu forte Redentor;\r\nEle é meu grande Salvador,\r\nQue me libertou na cruz, com mui amor,\r\nCristo é meu Rei e meu Senhor.', 1, NULL, NULL),
(79, 1, 'Quando me sinto fraco, não tenho mais vigor,\r\nNão vendo mais o brilho do sol, da clara luz;\r\nJesus está comigo, é meu Consolador;\r\nEle jamais me deixa, pois sempre me conduz.', 'Sua graça me basta a mim, a mim,\r\nSus graça me basta a mim;\r\nNa vida d’aquém, no céu também,\r\nSua graça me basta a mim.', 1, NULL, NULL),
(79, 2, 'Quando a tempestade vem contra mim feroz,\r\nNas trevas navegando com minha embarcação,\r\nCristo Jesus me anima, ouço a Sua voz;\r\n“Eis que estou contigo, por que temer, então?”', 'Sua graça me basta a mim, a mim,\r\nSus graça me basta a mim;\r\nNa vida d’aquém, no céu também,\r\nSua graça me basta a mim.', 1, NULL, NULL),
(79, 3, 'Quando m’atingem os dardos do inimigo audaz,\r\nQue busca com astúcia a vida me tirar,\r\nEu clamo, então, a Cristo, meu Salvador veraz,\r\nO qual me dá vitória, se nEle eu confiar.', 'Sua graça me basta a mim, a mim,\r\nSus graça me basta a mim;\r\nNa vida d’aquém, no céu também,\r\nSua graça me basta a mim.', 1, NULL, NULL),
(80, 1, 'Cristo te chama, a ti. pecador,\r\nTe chama a ti, com voz de amor;\r\nDeixa os pecados, e vem ao Senhor.\r\nCristo te chama a til', 'Cristo te chama, te chama a ti!\r\nCristo te chama, ó vem pecador!\r\nCristo te chama com voz de amor.\r\nCristo te chama a ti!', 1, NULL, NULL),
(80, 2, 'Deixa as sendas do mundo d’horror,\r\nVem a Jesus, vem com amor;\r\nCristo te salva, ó vem, pecador;\r\nCristo te chama a ti!', 'Cristo te chama, te chama a ti!\r\nCristo te chama, ó vem pecador!\r\nCristo te chama com voz de amor.\r\nCristo te chama a ti!', 1, NULL, NULL),
(80, 3, 'Cristo glorioso virá com poder;\r\nPronto estás para ascender?\r\nSó por Seu sangue tu podes vencer.\r\nCristo te chama a ti!', 'Cristo te chama, te chama a ti!\r\nCristo te chama, ó vem pecador!\r\nCristo te chama com voz de amor.\r\nCristo te chama a ti!', 1, NULL, NULL),
(80, 4, 'A compassiva voz do Salvador,\r\nChama-te já com santo amor;\r\nVem para a fonte, á vem pecador,\r\nCristo te chama a ti!', 'Cristo te chama, te chama a ti!\r\nCristo te chama, ó vem pecador!\r\nCristo te chama com voz de amor.\r\nCristo te chama a ti!', 1, NULL, NULL),
(81, 1, 'Somente em Cristo podemos\r\nA salvação receber;\r\nNovo concerto nós temos\r\nEm quem na cruz quis morrer.', 'Pode salvar, pode salvar,\r\nTira os pecados, e quer vos salvar,\r\nPode salvar, pode salvar,\r\nTira os pecados, e quer vos salvar.', 1, NULL, NULL),
(81, 2, 'Quando perdoa os pecados\r\nVida nos quer conceder.\r\nFomos por sangue comprados\r\nDo modo vão de viver.', 'Pode salvar, pode salvar,\r\nTira os pecados, e quer vos salvar,\r\nPode salvar, pode salvar,\r\nTira os pecados, e quer vos salvar.', 1, NULL, NULL),
(81, 3, 'Teu coração já entrega\r\nA quem te pode salvar;\r\nE quem as dores carrega:\r\nCristo te pode livrar!', 'Pode salvar, pode salvar,\r\nTira os pecados, e quer vos salvar,\r\nPode salvar, pode salvar,\r\nTira os pecados, e quer vos salvar.', 1, NULL, NULL),
(81, 4, 'Cristo te quer dar agora\r\nPaz e descanso real;\r\nQuer revelar-te esta hora:\r\nGraça imensa e real.', 'Pode salvar, pode salvar,\r\nTira os pecados, e quer vos salvar,\r\nPode salvar, pode salvar,\r\nTira os pecados, e quer vos salvar.', 1, NULL, NULL),
(82, 1, 'Um Salvador meigo eu tenho,\r\nA quem antes não conheci;\r\nAgora, alegre, eu venho\r\nContando a todos que O vi.', 'Todos, todos verão,\r\nTodos, todos verão;\r\nJesus glorioso e bondoso,\r\nSim, todos, todos verão.', 1, NULL, NULL),
(82, 2, 'De todos, é o mais formoso,\r\nImagem da glória de Deus;\r\nTornou-me alegre e ditoso,\r\nE herdeiro da glória dos céus.', 'Todos, todos verão,\r\nTodos, todos verão;\r\nJesus glorioso e bondoso,\r\nSim, todos, todos verão.', 1, NULL, NULL),
(82, 3, 'Se os ímpios me armarem cilada.\r\nNa senda que hei de trilhar,\r\nJesus me dará boa espada,\r\nE os laços eu hei de cortar.', 'Todos, todos verão,\r\nTodos, todos verão;\r\nJesus glorioso e bondoso,\r\nSim, todos, todos verão.', 1, NULL, NULL),
(82, 4, 'A vida e o amor Tu me deste,\r\nPorque Tu amaste a mim,\r\nTornou, sim, a glória celeste,\r\nMinha sorte bendita, enfim.', 'Todos, todos verão,\r\nTodos, todos verão;\r\nJesus glorioso e bondoso,\r\nSim, todos, todos verão.', 1, NULL, NULL),
(83, 1, 'Mais perto de Jesus, procuro sempre eu chegar,\r\nMais belo que o ouro do sol nado é a Ti mirar.\r\nEm pensamento, sonhos, tanta glória nunca vi;\r\nPois Ele é mais belo do que eu jamais previ!', 'Não posso explicar\r\nQuão meigo é Jesus;\r\nMas, face a face, no Teu lar,\r\nEu Te verei, Jesus!', 1, NULL, NULL),
(83, 2, 'A estrela resplendente da manhã á minha luz;\r\nO lírio dos vales é o bom Senhor Jesus;\r\nSuave e doce é o cheiro que só vem de Ti;\r\nPois Ele é mais belo do que eu jamais previ!', 'Não posso explicar\r\nQuão meigo é Jesus;\r\nMas, face a face, no Teu lar,\r\nEu Te verei, Jesus!', 1, NULL, NULL),
(83, 3, 'Se mágoas vêm me perturbar, o bálsamo Ele tem;\r\nMe toma nos Seus braços e, assim, descanso bem;\r\nNa cruz levou Jesus o meu pecado sobre Si;\r\nPois Ele é mais belo do que eu jamais previ!', 'Não posso explicar\r\nQuão meigo é Jesus;\r\nMas, face a face, no Teu lar,\r\nEu Te verei, Jesus!', 1, NULL, NULL),
(84, 2, 'Quem tem a fé de Abraão,\r\nO mundo sempre há de vencer;\r\nQuem quer ter firme o coração.\r\nPrecisa igualmente crer.', 'Sou Eu aquele, o grande “EU SOU”\r\nE, onde estais, também estou;\r\nNão disse, Eu, há muito já:\r\n“Pedi, pedi… dar-se-voa-á”?\r\nPedi com fé e com fervor\r\nE vos darei o Consolador.', 1, NULL, NULL),
(84, 3, 'Um terremoto e vento, após.\r\nDo céu, um fogo e mui furor.\r\nOuviu Elias a minha voz,\r\nVoz do Eterno, voz de amor.', 'Sou Eu aquele, o grande “EU SOU”\r\nE, onde estais, também estou;\r\nNão disse, Eu, há muito já:\r\n“Pedi, pedi… dar-se-voa-á”?\r\nPedi com fé e com fervor\r\nE vos darei o Consolador.', 1, NULL, NULL),
(84, 4, 'Um certo dia, Estêvão viu\r\nO céu aberto e viu-me a mim;\r\nApedrejado, sucumbiu,\r\nMas foi fiel, até o fim.', 'Sou Eu aquele, o grande “EU SOU”\r\nE, onde estais, também estou;\r\nNão disse, Eu, há muito já:\r\n“Pedi, pedi… dar-se-voa-á”?\r\nPedi com fé e com fervor\r\nE vos darei o Consolador.', 1, NULL, NULL),
(84, 5, 'Firmado em Mim, Rocha Eternal,\r\nAssim jamais o crente cai;\r\nBuscai o dom celestial,\r\nQue vem da casa de Meu Pai.', 'Sou Eu aquele, o grande “EU SOU”\r\nE, onde estais, também estou;\r\nNão disse, Eu, há muito já:\r\n“Pedi, pedi… dar-se-voa-á”?\r\nPedi com fé e com fervor\r\nE vos darei o Consolador.', 1, NULL, NULL),
(84, 1, 'Não perturbeis o coração,\r\nPorque Eu sempre sou fie!;\r\nEu fecho a boca do “leão”,\r\nNa cova estou com Daniel.', 'Sou Eu aquele, o grande “EU SOU”\r\nE, onde estais, também estou;\r\nNão disse, Eu, há muito já:\r\n“Pedi, pedi… dar-se-voa-á”?\r\nPedi com fé e com fervor\r\nE vos darei o Consolador.', 1, NULL, NULL),
(85, 1, 'Do Santo Espírito, está escrito,\r\nQue sois o templo, sim, templo de Deus;\r\nJá recebestes, depois que crestes,\r\nO dom glorioso descido do céu?', 'Deixai entrar, para morar,\r\nO Espírito de Deus;\r\nDom prometido, já concedido,\r\nBendito dom dos altos céus.', 1, NULL, NULL),
(85, 2, 'O que vos salva, também vos lava,\r\nP’ra vossa alma de manchas limpar;\r\nPurificados de vis pecados,\r\nO Santo Espírito já pode entrar.', 'Deixai entrar, para morar,\r\nO Espírito de Deus;\r\nDom prometido, já concedido,\r\nBendito dom dos altos céus.', 1, NULL, NULL),
(85, 3, 'Ó peregrinos, vede o destino,\r\nEntrai na terra de leite e mel,\r\nSantificados, por Deus chamados,\r\nSereis guiados, por guia fiel.', 'Deixai entrar, para morar,\r\nO Espírito de Deus;\r\nDom prometido, já concedido,\r\nBendito dom dos altos céus.', 1, NULL, NULL),
(86, 1, 'Oh! ’stou satisfeito com Cristo,\r\nPorque Ele minh’alma salvou;\r\nE sobre o madeiro, sofrendo,\r\nO Seu grande amor revelou.', 'Oh! ’stou satisfeito com Cristo,\r\nConfesso-O por meu Salvador:\r\nEmbora de nada ser digno,\r\nDesfruto do Seu grande amor.', 1, NULL, NULL),
(86, 2, 'Oh! ’stou satisfeito com Cristo,\r\nDe triste, tornei-me feliz,\r\nOuvindo o Seu santo Evangelho,\r\nE crendo no que Ele me diz.', 'Oh! ’stou satisfeito com Cristo,\r\nConfesso-O por meu Salvador:\r\nEmbora de nada ser digno,\r\nDesfruto do Seu grande amor.', 1, NULL, NULL),
(86, 3, 'Oh! ’stou satisfeito com Cristo,\r\nE sei que vai logo voltar;\r\nVirá com poder glorioso,\r\nA fim de Seu povo levar.', 'Oh! ’stou satisfeito com Cristo,\r\nConfesso-O por meu Salvador:\r\nEmbora de nada ser digno,\r\nDesfruto do Seu grande amor.', 1, NULL, NULL),
(87, 1, 'Justificado estou;\r\nCristo Jesus me livrou;\r\nEle é meu Mediador,\r\nTambém, meu bom Salvador.', 'Jesus, sou Teu, e Tu és meu;\r\nMe guiarás para o céu;\r\nCom graça e paz me satisfaz\r\nCristo, meu Mestre veraz.', 1, NULL, NULL),
(87, 2, 'Santificado fiquei,\r\nQuando a Ele roguei;\r\nEle me disse: tomai;\r\nDe minha graça usai.', 'Jesus, sou Teu, e Tu és meu;\r\nMe guiarás para o céu;\r\nCom graça e paz me satisfaz\r\nCristo, meu Mestre veraz.', 1, NULL, NULL),
(87, 3, 'Fui redimido, também,\r\nObra do meu Sumo Bem;\r\nTudo de graça ganhei;\r\nNada por obras da lei.', 'Jesus, sou Teu, e Tu és meu;\r\nMe guiarás para o céu;\r\nCom graça e paz me satisfaz\r\nCristo, meu Mestre veraz.', 1, NULL, NULL),
(87, 4, 'Ele a promessa me deu,\r\nDo Guia santo do céu;\r\nTambém me disse: ficai,\r\nE o batismo esperai.', 'Jesus, sou Teu, e Tu és meu;\r\nMe guiarás para o céu;\r\nCom graça e paz me satisfaz\r\nCristo, meu Mestre veraz.', 1, NULL, NULL),
(87, 5, 'Quando a Ele busquei,\r\nE batizado fiquei,\r\nLínguas estranhas falei,\r\nE meu Senhor exaltei.', 'Jesus, sou Teu, e Tu és meu;\r\nMe guiarás para o céu;\r\nCom graça e paz me satisfaz\r\nCristo, meu Mestre veraz.', 1, NULL, NULL),
(87, 6, 'Agora, vou trabalhar,\r\nE muitas almas ganhar;\r\nEssas que Ele salvou\r\nQuando na cruz expirou.', 'Jesus, sou Teu, e Tu és meu;\r\nMe guiarás para o céu;\r\nCom graça e paz me satisfaz\r\nCristo, meu Mestre veraz.', 1, NULL, NULL),
(88, 1, 'Jesus, meu Rei, Mestre e Senhor,\r\nO Teu amor revela a mim,\r\nEnquanto eu aqui viver,\r\nAté eu ver da vida o fim.', 'Revela a nós, Senhor Jesus, meu Salvador,\r\nAs maravilhas mil do Teu divino amor;\r\nE com veraz louvor, fervente gratidão;\r\nEleva a TI, Jesus Senhor, o nosso coração.', 1, NULL, NULL),
(88, 2, 'As bênçãos mil do Teu amor,\r\nQual esplendor me cercarão;\r\nO Teu olhar será, Jesus,\r\nA grata luz do coração.', 'Revela a nós, Senhor Jesus, meu Salvador,\r\nAs maravilhas mil do Teu divino amor;\r\nE com veraz louvor, fervente gratidão;\r\nEleva a TI, Jesus Senhor, o nosso coração.', 1, NULL, NULL),
(88, 3, 'Sorrisos Teus verei brilhar,\r\nSe não andar no mundo vil:\r\nDesfrutarei prazer veraz,\r\nTempo de paz primaveril.', 'Revela a nós, Senhor Jesus, meu Salvador,\r\nAs maravilhas mil do Teu divino amor;\r\nE com veraz louvor, fervente gratidão;\r\nEleva a TI, Jesus Senhor, o nosso coração.', 1, NULL, NULL),
(88, 4, 'E, quando for no céu morar,\r\nE descansar dos dias meus,\r\nFeliz viver receberei\r\nDe Ti, meu Rei, meu santo Deus!', 'Revela a nós, Senhor Jesus, meu Salvador,\r\nAs maravilhas mil do Teu divino amor;\r\nE com veraz louvor, fervente gratidão;\r\nEleva a TI, Jesus Senhor, o nosso coração.', 1, NULL, NULL),
(89, 1, 'Conta-me, sim, de Jesus,\r\nPara na mente, eu gravar\r\nComo foi que padeceu\r\nNa cruz, pra me libertar:\r\nA Sua paixão e morte,\r\nA começar no jardim,\r\nNão houve mais quem tivesse\r\nUm sofrimento assim!', 'Só amor! Só amor!\r\nSó amor! Sublime e grande amor!', 1, NULL, NULL),
(89, 2, 'Conta-me como sentiu\r\nA rude morte na cruz,\r\nSuportando a acusação\r\nDos inimigos da luz;\r\nConta-me como tem dado\r\nAo malfeitor Sua paz,\r\nDizendo: “Hoje, comigo,\r\nNo Paraíso estarás”.', 'Só amor! Só amor!\r\nSó amor! Sublime e grande amor!', 1, NULL, NULL),
(89, 3, 'Conta-me como chorou,\r\nFalando a Jerusalém;\r\nProfetas fazes morrer,\r\nQuando a ti eles vêm;\r\nConta-me como inda chora,\r\nPor causa do pecador,\r\nQue não quer Seu Evangelho,\r\nProclamado com amor.', 'Só amor! Só amor!\r\nSó amor! Sublime e grande amor!', 1, NULL, NULL),
(89, 4, 'Conta-me como o Senhor\r\nUm bom testemunho deu.\r\nSatisfazendo a lei,\r\nQuando o cálice bebeu;\r\nTambém disse estas palavras,\r\nCom Seu tão profundo amor;\r\nPai, lhes perdoa a cegueira,\r\nQue é própria do pecador.', 'Só amor! Só amor!\r\nSó amor! Sublime e grande amor!', 1, NULL, NULL),
(89, 5, 'Conta-me como, também,\r\nVeio ao povo judeu,\r\nPovo que tanto amou,\r\nQuerendo dar-lhe os céus;\r\nComo inda hoje Ele ama\r\nA todo o pecador,\r\nDando-lhe paz e descanso,\r\nPelo Evangelho de amor.', 'Só amor! Só amor!\r\nSó amor! Sublime e grande amor!', 1, NULL, NULL),
(90, 1, 'Há paz e alegria no reino da luz;\r\nÁ pátria dos remidos, Jesus nos conduz,\r\nNo reino dos céus há gozo e paz;\r\nOs filhos de Deus têm poder veraz.', NULL, 0, NULL, NULL),
(90, 2, 'Há paz e alegria no reino dos céus;\r\nOs salvos lá entoam louvores a Deus;\r\nDai glória a Jesus! Porque nos amou;\r\nPor nós o Seu sangue Ele derramou!', NULL, 0, NULL, NULL),
(90, 3, 'Jesus tem prometido o Consolador!\r\nO Espírito Santo, o instruidor;\r\nTambém prometeu, no reino dos céus,\r\nUm santo lugar, junto ao nosso Deus.', NULL, 0, NULL, NULL),
(90, 4, 'Firmado na promessa tão grande e eficaz,\r\nAgora neste mundo desfruto a paz;\r\nEspero Jesus, nas nuvens descer,\r\nE na Sua glória me receber.', NULL, 0, NULL, NULL);
